mapper.select:
SELECT dsd.id as splitId, dsd.storefront_id as shopId, dsd.number as number, dsd.create_date as createDate, dsd.ship_address as shipAddress, dsd.ship_name as shipName, dsd.ship_mobile as shipMobile, dsd.shipping_state as shippingState, dsd.total_amount as totalAmount, 1 as invoiceType, c.status, dsd.house_id as houseId, dsd.supplier_id as supId FROM dj_deliver_split_deliver dsd LEFT JOIN dj_complain c on c.business_id=dsd.id WHERE dsd.supplier_id =#{supId}
AND ( dsd.number LIKE CONCAT('%',#{searchKey},'%') or dsd.ship_address LIKE CONCAT('%',#{searchKey},'%') )
AND dsd.shipping_state=#{invoiceStatus}
and dsd.city_id=#{cityId} and dsd.shipping_state!=3 ORDER BY dsd.create_date desc

SELECT rmd.id as splitId, rmd.storefront_id as shopId, rmd.number as number, rmd.create_date as createDate, rmd.ship_address as shipAddress, rmd.ship_mobile as shipMobile, rmd.ship_name as shipName, rmd.shipping_state as shippingState, rmd.total_amount as totalAmount, 2 as invoiceType, rmd.house_id as houseId, rmd.supplier_id as supId FROM dj_repair_mend_deliver rmd WHERE rmd.supplier_id =#{supId}
AND ( rmd.number LIKE CONCAT('%',#{searchKey},'%') or rmd.ship_address LIKE CONCAT('%',#{searchKey},'%') )
AND rmd.shipping_state=#{invoiceStatus}
AND ( rmd.shipping_state=0 or rmd.shipping_state=1 )
and rmd.city_id=#{cityId} ORDER BY rmd.create_date desc

SELECT splitId, shopId, number, createDate, shipAddress, shipName, shipMobile, shippingState, totalAmount, invoiceType, STATUS, houseId, supId FROM ( SELECT dsd.id as splitId, dsd.storefront_id as shopId, dsd.number as number, dsd.create_date as createDate, dsd.ship_address as shipAddress, dsd.ship_name as shipName, dsd.ship_mobile as shipMobile, dsd.shipping_state as shippingState, dsd.total_amount as totalAmount, 1 as invoiceType, c.status, dsd.house_id as houseId, dsd.supplier_id as supId FROM dj_deliver_split_deliver dsd LEFT JOIN dj_complain c on c.business_id=dsd.id WHERE dsd.supplier_id =#{supId}
AND ( dsd.number LIKE CONCAT('%',#{searchKey},'%') or dsd.ship_address LIKE CONCAT('%',#{searchKey},'%') )
and dsd.city_id=#{cityId} and dsd.shipping_state!=3 UNION ALL SELECT rmd.id as splitId, rmd.storefront_id as shopId, rmd.number as number, rmd.create_date as createDate, rmd.ship_address as shipAddress, rmd.ship_mobile as shipMobile, rmd.ship_name as shipName, rmd.shipping_state as shippingState, rmd.total_amount as totalAmount, 2 as invoiceType, null as status, rmd.house_id as houseId, rmd.supplier_id as supId FROM dj_repair_mend_deliver rmd WHERE rmd.supplier_id =#{supId}
AND ( rmd.number LIKE CONCAT('%',#{searchKey},'%') or rmd.ship_address LIKE CONCAT('%',#{searchKey},'%') )
and rmd.city_id=#{cityId} )a ORDER BY createDate desc

mapper.update:
UPDATE

    dj_deliver_split_deliver
    set shipping_state=1


    dj_repair_mend_deliver
    set shipping_state=#{shippingState}

where id=#{id}

mapper.select:
SELECT storefront_id as shopId, sum(count) as count, supplier_id as supId FROM ( SELECT supplier_id, count(0) AS count, storefront_id FROM dj_deliver_split_deliver WHERE supplier_id = #{supId}
and apply_state = 0
and ( apply_state =1 or apply_state=2 )
and city_id=#{cityId}
and storefront_id in
#{item.id}
GROUP BY storefront_id UNION ALL SELECT supplier_id, count(0) AS count, storefront_id FROM dj_repair_mend_deliver WHERE supplier_id = #{supId}
and apply_state = 0
and ( apply_state =1 or apply_state=2 )
and city_id=#{cityId}
and storefront_id in
#{item.id}
GROUP BY storefront_id ) a GROUP BY storefront_id

SELECT number, supplier_id as supId, storefront_id as shopId, create_date as createDate, ship_address as shipAddress, ship_name as shipName, apply_state as applyState, invoiceType, sum(applyMoney) as applyMoney, splitId, houseId FROM ( SELECT dsd.number, dsd.supplier_id, dsd.storefront_id, dos.create_date, dsd.ship_address, dsd.ship_name, dsd.apply_state, dsd.id as splitId, 1 as invoiceType, dsd.apply_money as applyMoney, dsd.house_id as houseId FROM dj_deliver_split_deliver dsd INNER JOIN dj_deliver_order_split dos ON dsd.order_split_id = dos.id UNION ALL SELECT rmd.number, rmd.supplier_id, rmd.storefront_id, rmo.create_date, rmd.ship_address, rmd.ship_name, rmd.apply_state, rmd.id as splitId, 2 as invoiceType, rmd.apply_money as applyMoney, rmd.house_id as houseId FROM dj_repair_mend_deliver rmd INNER JOIN dj_repair_mend_order rmo ON rmd.mend_order_id = rmo.id ) a WHERE supplier_id =#{supId} AND storefront_id =#{shopId}
and apply_state = 0
and ( apply_state =1 or apply_state=2 )

SELECT dsd.supplier_id AS supId, dsd.ship_address AS shipAddress, m. NAME, m.mobile, sum(dosi.shop_count) AS supplyQuantity, sum(dsd.total_amount) AS income, h.id AS houseId FROM dj_deliver_split_deliver dsd INNER JOIN dj_house h ON dsd.house_id = h.id INNER JOIN dj_member m ON m.id = h.member_id INNER JOIN dj_deliver_order_split_item dosi ON dsd.id = dosi.split_deliver_id WHERE dsd.supplier_id = #{supId}
AND ( dsd.ship_address LIKE CONCAT('%',#{searchKey},'%') or m. NAME LIKE CONCAT('%',#{searchKey},'%') or m.mobile LIKE CONCAT('%',#{searchKey},'%') )
and dsd.city_id=#{cityId} GROUP BY h.id

SELECT dsd.id AS orderSplitId, dsd.number, dsd.send_time as createDate, sum(dosi.shop_count) as shopCount, dsd.total_amount as totalAmount FROM dj_deliver_split_deliver dsd INNER JOIN dj_deliver_order_split_item dosi ON dsd.id = dosi.split_deliver_id WHERE dsd.storefront_id = #{shopId} AND dsd.supplier_id = #{supId} and dsd.city_id=#{cityId}
AND dsd.number LIKE CONCAT('%',#{searchKey},'%')
GROUP BY dsd.number

SELECT bsp.image, -- 图片 bsp.product_name AS productName, -- 商品名称 bpt.product_sn AS productSn, -- 商品编号 bsp.prod_template_id AS prodTemplateId, -- 商品id doi.shop_count AS shopCount, -- 总购买数 bsp.supplied_num AS suppliedNum, -- 供货数 IFNULL(sum(doi.cost),'') AS cost, -- 成本总价 IFNULL(sum(doi.price),'') AS price, -- 销售总价 IFNULL( sum(doi.price) - sum(doi.cost), '' ) AS profit, -- 利润 doi.product_id AS productId -- 商品id FROM dj_basics_storefront_product bsp -- 店铺商品 INNER JOIN dj_basics_product_template bpt ON bsp.prod_template_id = bpt.id -- 商品模板 INNER JOIN dj_deliver_order_item doi ON doi.product_id = bpt.id -- 订单详情 WHERE 1 = 1 AND bsp.data_status = 0 AND bsp.is_shelf_status = 1 AND bsp.storefront_id = #{storefrontId}
AND ( bsp.product_name LIKE CONCAT('%',#{searchKey},'%') or bpt.product_sn LIKE CONCAT('%', #{searchKey},'%') )
GROUP BY bsp.prod_template_id

SELECT IFNULL(dsd.storefront_id ,'') as storefrontId, -- 店铺id IFNULL(dsd.ship_address ,'')AS shipAddress, -- 房子地址 IFNULL(m.NAME,'') as name, -- 业主姓名 IFNULL(sum(dsd.total_amount),'') AS income, -- 收入 IFNULL(sum(dsd.apply_money),'') AS expenditure,-- 支出 IFNULL(sum(dsd.total_amount)-sum(dsd.apply_money),'') AS profit, -- 利润 h.id AS houseId -- 房子id FROM dj_deliver_split_deliver dsd -- 后台生成发货单 INNER JOIN dj_house h ON dsd.house_id = h.id -- 房子 INNER JOIN dj_member m ON m.id = h.member_id -- 工人 INNER JOIN dj_deliver_order_split_item dosi ON dsd.id = dosi.split_deliver_id -- 要货单明细 WHERE dsd.storefront_id = #{storefrontId}
AND ( dsd.ship_address LIKE CONCAT('%',#{searchKey},'%') or m.NAME LIKE CONCAT('%',#{searchKey},'%') )
and dsd.city_id=#{cityId} GROUP BY h.id

SELECT IFNULL(dsd.storefront_id ,'') as storefrontId, -- 店铺id IFNULL(dsd.ship_address ,'')AS shipAddress, -- 房子地址 m.NAME, -- 业主姓名 m.mobile,-- 电话号码 IFNULL(sum(dsd.total_amount),'') AS income, -- 收入 IFNULL(sum(dsd.apply_money),'') AS expenditure,-- 支出 IFNULL(sum(dsd.total_amount)-sum(dsd.apply_money),'')AS profit, -- 利润 h.id AS houseId -- 房子id FROM dj_deliver_split_deliver dsd -- 后台生成发货单 INNER JOIN dj_house h ON dsd.house_id = h.id -- 房子 INNER JOIN dj_member m ON m.id = h.member_id -- 工人 INNER JOIN dj_deliver_order_split_item dosi ON dsd.id = dosi.split_deliver_id -- 要货单明细 WHERE dsd.storefront_id = #{storefrontId}
AND ( dsd.ship_address LIKE CONCAT('%',#{searchKey},'%') or m.NAME LIKE CONCAT('%',#{searchKey},'%') )
and dsd.city_id=#{cityId} GROUP BY h.id
