mapper.select:
SELECT dsd.id, dsd.storefront_id as shopId, dsd.number as number, dsd.create_date as createDate, dsd.ship_address as shipAddress, dsd.ship_name as shipName, dsd.ship_mobile as shipMobile, dsd.shipping_state as shippingState, dsd.total_amount as totalAmount, dsd.order_split_id as splitId, 1 as invoiceType, c.status FROM dj_deliver_split_deliver dsd LEFT JOIN dj_complain c on c.business_id=dsd.id WHERE dsd.supplier_id =#{supId}
AND ( dsd.number LIKE CONCAT('%',#{searchKey},'%') or dsd.ship_address LIKE CONCAT('%',#{searchKey},'%') )
AND dsd.shipping_state=#{invoiceStatus}
and dsd.city_id=#{cityId}

SELECT rmd.id, rmd.storefront_id as shopId, rmd.number as number, rmd.create_date as createDate, rmd.ship_address as shipAddress, rmd.ship_mobile as shipMobile, rmd.ship_name as shipName, rmd.shipping_state as shippingState, rmd.total_amount as totalAmount, rmd.mend_order_id as splitId, 2 as invoiceType FROM dj_repair_mend_deliver rmd WHERE rmd.supplier_id =#{supId}
AND ( rmd.number LIKE CONCAT('%',#{searchKey},'%') or rmd.ship_address LIKE CONCAT('%',#{searchKey},'%') )
AND rmd.shipping_state=#{invoiceStatus}
and rmd.city_id=#{cityId}

SELECT dsd.id, dsd.storefront_id as shopId, dsd.number as number, dsd.create_date as createDate, dsd.ship_address as shipAddress, dsd.ship_name as shipName, dsd.ship_mobile as shipMobile, dsd.shipping_state as shippingState, dsd.total_amount as totalAmount, dsd.order_split_id as splitId, 1 as invoiceType, c.status FROM dj_deliver_split_deliver dsd LEFT JOIN dj_complain c on c.business_id=dsd.id WHERE dsd.supplier_id =#{supId}
AND ( dsd.number LIKE CONCAT('%',#{searchKey},'%') or dsd.ship_address LIKE CONCAT('%',#{searchKey},'%') )
and dsd.city_id=#{cityId} UNION ALL SELECT rmd.id, rmd.storefront_id as shopId, rmd.number as number, rmd.create_date as createDate, rmd.ship_address as shipAddress, rmd.ship_mobile as shipMobile, rmd.ship_name as shipName, rmd.shipping_state as shippingState, rmd.total_amount as totalAmount, rmd.mend_order_id as splitId, 2 as invoiceType, null as status FROM dj_repair_mend_deliver rmd WHERE rmd.supplier_id =#{supId}
AND ( rmd.number LIKE CONCAT('%',#{searchKey},'%') or rmd.ship_address LIKE CONCAT('%',#{searchKey},'%') )
and rmd.city_id=#{cityId}

mapper.update:
UPDATE

    dj_deliver_split_deliver
    set shipping_state=1


    dj_repair_mend_deliver
    set shipping_state=#{shippingState}

where id=#{id}

mapper.select:
SELECT storefront_id as shopId, sum(count) as count, supplier_id as supId FROM ( SELECT supplier_id, count(0) AS count, storefront_id FROM dj_deliver_split_deliver WHERE supplier_id = #{supId}
and apply_state = 0
and ( apply_state =1 or apply_state=2 )
and city_id=#{cityId}
and storefront_id in
#{item.id}
GROUP BY storefront_id UNION ALL SELECT supplier_id, count(0) AS count, storefront_id FROM dj_repair_mend_deliver WHERE supplier_id = #{supId}
and apply_state = 0
and ( apply_state =1 or apply_state=2 )
and city_id=#{cityId}
and storefront_id in
#{item.id}
GROUP BY storefront_id ) a GROUP BY storefront_id

SELECT number, supplier_id as supId, storefront_id as shopId, create_date as createDate, ship_address as shipAddress, ship_name as shipName, apply_state as applyState, invoiceType, sum(applyMoney) as applyMoney, splitId FROM ( SELECT dsd.number, dsd.supplier_id, dsd.storefront_id, dos.create_date, dsd.ship_address, dsd.ship_name, dsd.apply_state, dsd.order_split_id as splitId, 1 as invoiceType, dsd.apply_money as applyMoney FROM dj_deliver_split_deliver dsd INNER JOIN dj_deliver_order_split dos ON dsd.order_split_id = dos.id UNION ALL SELECT rmd.number, rmd.supplier_id, rmd.storefront_id, rmo.create_date, rmd.ship_address, rmd.ship_name, rmd.apply_state, rmd.mend_order_id as splitId, 2 as invoiceType, rmd.apply_money as applyMoney FROM dj_repair_mend_deliver rmd INNER JOIN dj_repair_mend_order rmo ON rmd.mend_order_id = rmo.id ) a WHERE supplier_id =#{supId} AND storefront_id =#{shopId}
and apply_state = 0
and ( apply_state =1 or apply_state=2 )

SELECT dsd.supplier_id AS supId, dsd.ship_address AS shipAddress, m. NAME, m.mobile, sum(dosi.shop_count) AS supplyQuantity, sum(dsd.total_amount) AS income, h.id AS houseId FROM dj_deliver_split_deliver dsd INNER JOIN dj_house h ON dsd.house_id = h.id INNER JOIN dj_member m ON m.id = h.member_id INNER JOIN dj_deliver_order_split_item dosi ON dsd.id = dosi.split_deliver_id WHERE dsd.supplier_id = #{supId}
AND ( dsd.ship_address LIKE CONCAT('%',#{searchKey},'%') or m. NAME LIKE CONCAT('%',#{searchKey},'%') or m.mobile LIKE CONCAT('%',#{searchKey},'%') )
and dsd.city_id=#{cityId} GROUP BY h.id

SELECT dsd.order_split_id AS orderSplitId, dsd.number, dsd.send_time as createDate, sum(dosi.shop_count) as shopCount, dsd.total_amount as totalAmount FROM dj_deliver_split_deliver dsd INNER JOIN dj_deliver_order_split_item dosi ON dsd.id = dosi.split_deliver_id WHERE dsd.storefront_id = #{shopId} AND dsd.supplier_id = #{supId} and dsd.city_id=#{cityId}
AND dsd.number LIKE CONCAT('%',#{searchKey},'%')
GROUP BY dsd.number
