mapper.select:
SELECT rmd.id as splitId, rmd.storefront_id as shopId, rmd.number as number, rmd.create_date as createDate, rmd.ship_address as shipAddress, rmd.ship_mobile as shipMobile, rmd.ship_name as shipName, (case when rmd.shipping_state=6 then 1 else rmd.shipping_state end) as shippingState, rmd.total_amount as totalAmount, rmd.apply_money applyMoney, 2 as invoiceType, rmd.house_id as houseId, rmd.supplier_id as supId FROM dj_repair_mend_deliver rmd WHERE rmd.supplier_id =#{supId}
AND ( rmd.number LIKE CONCAT('%',#{searchKey},'%') or rmd.ship_address LIKE CONCAT('%',#{searchKey},'%') )
AND rmd.shipping_state in(1,6)
AND rmd.shipping_state=#{invoiceStatus}
and rmd.shipping_state in(0,1,2,4,5,6,7,8) and rmd.city_id=#{cityId} ORDER BY rmd.create_date desc

SELECT splitId, shopId, number, createDate, shipAddress, shipName, shipMobile, shippingState, totalAmount, invoiceType, STATUS, houseId, supId FROM ( SELECT dsd.id as splitId, dsd.storefront_id as shopId, dsd.number as number, dsd.create_date as createDate, dsd.ship_address as shipAddress, dsd.ship_name as shipName, dsd.ship_mobile as shipMobile, dsd.shipping_state as shippingState, dsd.total_amount as totalAmount, 1 as invoiceType, c.status, dsd.house_id as houseId, dsd.supplier_id as supId FROM dj_deliver_split_deliver dsd LEFT JOIN dj_complain c on c.business_id=dsd.id WHERE dsd.supplier_id =#{supId}
AND ( dsd.number LIKE CONCAT('%',#{searchKey},'%') or dsd.ship_address LIKE CONCAT('%',#{searchKey},'%') )
and dsd.city_id=#{cityId} and dsd.shipping_state!=3 UNION ALL SELECT rmd.id as splitId, rmd.storefront_id as shopId, rmd.number as number, rmd.create_date as createDate, rmd.ship_address as shipAddress, rmd.ship_mobile as shipMobile, rmd.ship_name as shipName, rmd.shipping_state as shippingState, rmd.total_amount as totalAmount, 2 as invoiceType, null as status, rmd.house_id as houseId, rmd.supplier_id as supId FROM dj_repair_mend_deliver rmd WHERE rmd.supplier_id =#{supId}
AND ( rmd.number LIKE CONCAT('%',#{searchKey},'%') or rmd.ship_address LIKE CONCAT('%',#{searchKey},'%') )
and rmd.city_id=#{cityId} )a ORDER BY createDate desc

mapper.update:
UPDATE

    dj_deliver_split_deliver
    set shipping_state=1


    dj_repair_mend_deliver
    set shipping_state=#{shippingState}

where id=#{id}

mapper.select:
SELECT IFNULL(storefront_id,'') as shopId, IFNULL(sum(count),'0') as count, IFNULL(supplier_id,'') as supId FROM ( SELECT supplier_id, count(0) AS count, storefront_id FROM dj_deliver_split_deliver WHERE supplier_id = #{supId}
and apply_state = 0
and ( apply_state =1 or apply_state=2 )
and city_id=#{cityId}
and storefront_id in
#{item.id}
GROUP BY storefront_id UNION ALL SELECT supplier_id, count(0) AS count, storefront_id FROM dj_repair_mend_deliver WHERE supplier_id = #{supId}
and apply_state = 0
and ( apply_state =1 or apply_state=2 )
and city_id=#{cityId}
and storefront_id in
#{item.id}
GROUP BY storefront_id ) a GROUP BY storefront_id

SELECT number, supplier_id as supId, storefront_id as shopId, create_date as createDate, ship_address as shipAddress, ship_name as shipName, apply_state as applyState, invoiceType, sum(applyMoney) as applyMoney, splitId, houseId FROM ( SELECT dsd.number, dsd.supplier_id, dsd.storefront_id, dos.create_date, dsd.ship_address, dsd.ship_name, dsd.apply_state, dsd.id as splitId, 1 as invoiceType, dsd.apply_money as applyMoney, dsd.house_id as houseId FROM dj_deliver_split_deliver dsd INNER JOIN dj_deliver_order_split dos ON dsd.order_split_id = dos.id UNION ALL SELECT rmd.number, rmd.supplier_id, rmd.storefront_id, rmo.create_date, rmd.ship_address, rmd.ship_name, rmd.apply_state, rmd.id as splitId, 2 as invoiceType, rmd.apply_money as applyMoney, rmd.house_id as houseId FROM dj_repair_mend_deliver rmd INNER JOIN dj_repair_mend_order rmo ON rmd.mend_order_id = rmo.id ) a WHERE supplier_id =#{supId} AND storefront_id =#{shopId}
and apply_state = 0
and ( apply_state =1 or apply_state=2 )

SELECT dsd.supplier_id AS supId, dsd.ship_address AS shipAddress, m. NAME, m.mobile, sum(dosi.shop_count) AS supplyQuantity, sum(dsd.total_amount) AS income, h.id AS houseId FROM dj_deliver_split_deliver dsd INNER JOIN dj_house h ON dsd.house_id = h.id INNER JOIN dj_member m ON m.id = h.member_id INNER JOIN dj_deliver_order_split_item dosi ON dsd.id = dosi.split_deliver_id WHERE dsd.supplier_id = #{supId}
AND ( dsd.ship_address LIKE CONCAT('%',#{searchKey},'%') or m. NAME LIKE CONCAT('%',#{searchKey},'%') or m.mobile LIKE CONCAT('%',#{searchKey},'%') )
and dsd.city_id=#{cityId} GROUP BY h.id

SELECT dsd.id AS orderSplitId, dsd.number, dsd.send_time as createDate, sum(dosi.shop_count) as shopCount, dsd.total_amount as totalAmount FROM dj_deliver_split_deliver dsd INNER JOIN dj_deliver_order_split_item dosi ON dsd.id = dosi.split_deliver_id WHERE dsd.storefront_id = #{shopId} AND dsd.supplier_id = #{supId} and dsd.city_id=#{cityId}
AND dsd.number LIKE CONCAT('%',#{searchKey},'%')
GROUP BY dsd.number

SELECT mt.image, mt.productId, mt.productName, mt.productSn, sum(mt.receive) receive, sum(mt.returnCount) returnCount, sum(mt.totalStevedorageCost) totalStevedorageCost, sum(mt.totalTransportationCost) totalTransportationCost, sum(mt.totalPrice) totalPrice, sum(mt.totalSupCost) totalSupCost, sum(mt.profit) profit, sum(mt.totalPrice) income, sum(mt.totalSupCost+mt.totalStevedorageCost+mt.totalTransportationCost) expenditure FROM ( SELECT tt.image, tt.productId, tt.productName, tt.productSn, sum(tt.receive) receive, 0 returnCount, sum(tt.sup_stevedorage_cost) totalStevedorageCost, sum(tt.sup_transportation_cost) totalTransportationCost, sum( tt.price * tt.receive + tt.transportation_cost + tt.stevedorage_cost-tt.discount_price ) totalPrice, tt.supPrice * tt.receive totalSupCost, sum( tt.price * tt.receive + tt.transportation_cost + tt.stevedorage_cost-tt.discount_price ) - sum( tt.supPrice * tt.receive + tt.sup_stevedorage_cost + tt.sup_transportation_cost ) profit FROM ( SELECT bsp.image, bsp.product_name AS productName, bpt.product_sn AS productSn, IFNULL( ( CASE WHEN s.shipping_state = 4 AND s.complain_status = 3 THEN t.num ELSE t.receive END ), t.num ) AS receive, t.sup_cost AS supPrice, t.price AS price, IFNULL(t.transportation_cost,0) transportation_cost, IFNULL(t.stevedorage_cost,0) stevedorage_cost, IFNULL(t.sup_transportation_cost,0) sup_transportation_cost, IFNULL(t.sup_stevedorage_cost,0) sup_stevedorage_cost, IFNULL(t.discount_price,0) discount_price, t.product_id AS productId FROM dj_deliver_split_deliver s INNER JOIN dj_deliver_order_split_item t ON s.id = t.split_deliver_id INNER JOIN dj_basics_storefront_product bsp ON t.product_id = bsp.id INNER JOIN dj_basics_product_template bpt ON bsp.prod_template_id = bpt.id WHERE s.storefront_id = #{storefrontId}
AND ( bsp.product_name LIKE CONCAT('%',#{searchKey},'%') or bpt.product_sn LIKE CONCAT('%', #{searchKey},'%') )
) tt GROUP BY tt.productId UNION ALL SELECT tt.image, tt.productId, tt.productName, tt.productSn, 0 receiver, sum(tt.returnCount) returnCount, sum(tt.stevedorage_cost*(-1)) totalStevedorageCost, sum(tt.transportation_cost*(-1)) totalTransportationCost, sum( tt.supPrice * tt.returnCount - tt.sup_stevedorage_cost - tt.sup_transportation_cost ) totalPrice, tt.price * tt.returnCount-tt.discount_price totalSupCost, sum( tt.supPrice * tt.returnCount - tt.sup_stevedorage_cost - tt.sup_transportation_cost ) - sum( tt.price * tt.returnCount - tt.transportation_cost - tt.stevedorage_cost-tt.discount_price ) profit FROM ( SELECT bsp.image, bsp.product_name AS productName, bpt.product_sn AS productSn, IFNULL( ( CASE WHEN s.shipping_state = 7 THEN t.shop_count ELSE t.actual_count END ), t.shop_count ) AS returnCount, t.cost AS supPrice, t.price AS price, IFNULL(t.transportation_cost,0) transportation_cost, IFNULL(t.stevedorage_cost,0) stevedorage_cost, IFNULL(t.sup_transportation_cost,0) sup_transportation_cost, IFNULL(t.sup_stevedorage_cost,0) sup_stevedorage_cost, IFNULL(t.discount_price,0) discount_price, t.product_id AS productId FROM dj_repair_mend_deliver s INNER JOIN dj_repair_mend_materiel t ON s.id = t.repair_mend_deliver_id INNER JOIN dj_basics_storefront_product bsp ON t.product_id = bsp.id INNER JOIN dj_basics_product_template bpt ON bsp.prod_template_id = bpt.id INNER JOIN dj_repair_mend_order m ON s.mend_order_id = m.id WHERE s.storefront_id = #{storefrontId}
AND ( bsp.product_name LIKE CONCAT('%',#{searchKey},'%') or bpt.product_sn LIKE CONCAT('%', #{searchKey},'%') )
AND m.type IN (2, 5) ) tt GROUP BY tt.productId ) mt GROUP BY mt.productId ORDER BY mt.productId

SELECT sum( tt.price * tt.receive + tt.transportation_cost + tt.stevedorage_cost-tt.discount_price ) income, sum( tt.supPrice * tt.receive + tt.sup_stevedorage_cost + tt.sup_transportation_cost ) expenditure, sum( tt.price * tt.receive + tt.transportation_cost + tt.stevedorage_cost-tt.discount_price ) - sum( tt.supPrice * tt.receive + tt.sup_stevedorage_cost + tt.sup_transportation_cost ) profit FROM ( SELECT IFNULL( ( CASE WHEN s.shipping_state = 4 AND s.complain_status = 3 THEN t.num ELSE t.receive END ), t.num ) AS receive, t.sup_cost AS supPrice, t.price AS price, IFNULL(t.transportation_cost, 0) transportation_cost, IFNULL(t.stevedorage_cost, 0) stevedorage_cost, IFNULL( t.sup_transportation_cost, 0 ) sup_transportation_cost, IFNULL(t.sup_stevedorage_cost, 0) sup_stevedorage_cost, IFNULL(t.discount_price,0) discount_price, s.id splitId FROM dj_deliver_split_deliver s INNER JOIN dj_deliver_order_split_item t ON s.id = t.split_deliver_id INNER JOIN dj_member_address m on (s.address_id=m.id or s.house_id=m.house_id) WHERE s.storefront_id = #{storefrontId}
AND t.product_id = #{productId}
and m.id =#{addressId}
and m.house_id=#{houseId}
) tt

SELECT tt.splitId, tt.number, tt.sendTime, tt.address, sum(tt.receive) receive, 0 returnCount, sum(tt.sup_stevedorage_cost) totalStevedorageCost, sum(tt.sup_transportation_cost) totalTransportationCost, sum( tt.price * tt.receive + tt.transportation_cost + tt.stevedorage_cost-tt.discount_price ) totalPrice, tt.supPrice * tt.receive totalSupCost, sum( tt.price * tt.receive + tt.transportation_cost + tt.stevedorage_cost-tt.discount_price ) - sum( tt.supPrice * tt.receive + tt.sup_stevedorage_cost + tt.sup_transportation_cost ) profit FROM ( SELECT IFNULL( ( CASE WHEN s.shipping_state = 4 AND s.complain_status = 3 THEN t.num ELSE t.receive END ), t.num ) AS receive, t.sup_cost AS supPrice, t.price AS price, IFNULL(t.transportation_cost, 0) transportation_cost, IFNULL(t.stevedorage_cost, 0) stevedorage_cost, IFNULL( t.sup_transportation_cost, 0 ) sup_transportation_cost, IFNULL(t.sup_stevedorage_cost, 0) sup_stevedorage_cost, IFNULL(t.discount_price,0) discount_price, s.id splitId, s.number number, s.create_date sendTime, m.address FROM dj_deliver_split_deliver s INNER JOIN dj_deliver_order_split_item t ON s.id = t.split_deliver_id INNER JOIN dj_member_address m on (s.address_id=m.id or s.house_id=m.house_id) WHERE s.storefront_id = #{storefrontId}
AND t.product_id = #{productId}
and m.id =#{addressId}
and m.house_id=#{houseId}
) tt GROUP BY tt.splitId order by tt.sendTime desc

SELECT sum( tt.supPrice * tt.returnCount - tt.sup_stevedorage_cost - tt.sup_transportation_cost ) income, sum( tt.price * tt.returnCount - tt.stevedorage_cost - tt.transportation_cost-tt.discount_price ) expenditure, sum( tt.supPrice * tt.returnCount - tt.sup_stevedorage_cost - tt.sup_transportation_cost ) - sum( tt.price * tt.returnCount - tt.transportation_cost - tt.stevedorage_cost-tt.discount_price ) profit FROM ( SELECT s.id splitId, IFNULL( ( CASE WHEN s.shipping_state = 7 THEN t.shop_count ELSE t.actual_count END ), t.shop_count ) AS returnCount, t.cost AS supPrice, t.price AS price, IFNULL(t.transportation_cost, 0) transportation_cost, IFNULL(t.stevedorage_cost, 0) stevedorage_cost, IFNULL( t.sup_transportation_cost, 0 ) sup_transportation_cost, IFNULL(t.discount_price,0) discount_price, IFNULL(t.sup_stevedorage_cost, 0) sup_stevedorage_cost FROM dj_repair_mend_deliver s INNER JOIN dj_repair_mend_materiel t ON s.id = t.repair_mend_deliver_id INNER JOIN dj_basics_storefront_product bsp ON t.product_id = bsp.id INNER JOIN dj_basics_product_template bpt ON bsp.prod_template_id = bpt.id INNER JOIN dj_repair_mend_order m ON s.mend_order_id = m.id INNER JOIN dj_member_address ma on (s.address_id=ma.id or s.house_id=ma.house_id) WHERE s.storefront_id = #{storefrontId}
AND t.product_id = #{productId}
and ma.id =#{addressId}
and ma.house_id=#{houseId}
AND m.type IN (2, 5) ) tt

SELECT tt.splitId, tt.number, tt.sendTime, 0 receiver, tt.address, sum(tt.returnCount) returnCount, sum(tt.sup_stevedorage_cost) totalStevedorageCost, sum(tt.sup_transportation_cost) totalTransportationCost, sum( tt.price * tt.returnCount - tt.stevedorage_cost - tt.transportation_cost - tt.discount_price ) totalPrice, tt.supPrice * tt.returnCount totalSupCost, sum( tt.supPrice * tt.returnCount - tt.sup_stevedorage_cost - tt.sup_transportation_cost ) - sum( tt.price * tt.returnCount - tt.transportation_cost - tt.stevedorage_cost-tt.discount_price ) profit FROM ( SELECT s.id splitId, s.number, m.create_date sendTime, ma.address, IFNULL( ( CASE WHEN s.shipping_state = 7 THEN t.shop_count ELSE t.actual_count END ), t.shop_count ) AS returnCount, t.cost AS supPrice, t.price AS price, IFNULL(t.transportation_cost, 0) transportation_cost, IFNULL(t.stevedorage_cost, 0) stevedorage_cost, IFNULL( t.sup_transportation_cost, 0 ) sup_transportation_cost, IFNULL(t.discount_price,0) discount_price, IFNULL(t.sup_stevedorage_cost, 0) sup_stevedorage_cost FROM dj_repair_mend_deliver s INNER JOIN dj_repair_mend_materiel t ON s.id = t.repair_mend_deliver_id INNER JOIN dj_basics_storefront_product bsp ON t.product_id = bsp.id INNER JOIN dj_basics_product_template bpt ON bsp.prod_template_id = bpt.id INNER JOIN dj_repair_mend_order m ON s.mend_order_id = m.id INNER JOIN dj_member_address ma on (s.address_id=ma.id or s.house_id=ma.house_id) WHERE s.storefront_id = #{storefrontId}
AND t.product_id = #{productId}
and ma.id =#{addressId}
and ma.house_id=#{houseId}
AND m.type IN (2, 5) ) tt GROUP BY tt.splitId order by tt.sendTime desc

select tt.storefrontId,sum(tt.income) income,sum(tt.expenditure) expenditure,sum(tt.profit) profit,tt.supId,tt.name,tt.telephone,tt.checkPeople from ( SELECT dsd.storefront_id AS storefrontId, sum(IFNULL(dsd.total_amount, 0)) AS income, sum(IFNULL(dsd.apply_money, 0)) AS expenditure, sum(IFNULL(dsd.total_amount, 0)) - sum(IFNULL(dsd.apply_money, 0)) AS profit, dsd.supplier_id AS supId, t.`name` NAME, t.telephone, t.check_people checkPeople, dsd.create_date createDate FROM dj_deliver_split_deliver dsd, dj_supplier t WHERE dsd.supplier_id = t.id AND dsd.storefront_id = #{storefrontId}
and dsd.create_date>=#{startTime}
and dsd.create_date<=#{endTime}
AND ( t.name LIKE CONCAT('%',#{searchKey},'%') or t.telephone LIKE CONCAT('%', #{searchKey},'%') or t.check_people LIKE CONCAT('%', #{searchKey},'%') )
group by dsd.supplier_id union all SELECT dsd.storefront_id AS storefrontId, sum(IFNULL(dsd.apply_money, 0)) AS income, sum(IFNULL(dsd.total_amount, 0)) AS expenditure, (sum(IFNULL(dsd.apply_money, 0))- sum(IFNULL(dsd.total_amount, 0))) AS profit, dsd.supplier_id AS supId, t.`name` NAME, t.telephone, t.check_people checkPeople, dsd.create_date createDate FROM dj_repair_mend_deliver dsd, dj_repair_mend_order ss, dj_supplier t WHERE dsd.supplier_id = t.id AND dsd.storefront_id = #{storefrontId} and dsd.mend_order_id=ss.id and ss.type in(2,5)
and dsd.create_date>=#{startTime}
and dsd.create_date<DATE_ADD(#{endTime},INTERVAL 1 DAY)
AND ( t.name LIKE CONCAT('%',#{searchKey},'%') or t.telephone LIKE CONCAT('%', #{searchKey},'%') or t.check_people LIKE CONCAT('%', #{searchKey},'%') )
group by dsd.supplier_id ) tt group by tt.supId order by tt.createDate DESC

SELECT IFNULL(dsd.storefront_id ,'') as storefrontId, -- 店铺id IFNULL(dsd.ship_address ,'')AS shipAddress, -- 房子地址 IFNULL(m.NAME,'') as name, -- 业主姓名 IFNULL(sum(dsd.total_amount),'') AS income, -- 收入 IFNULL(sum(dsd.apply_money),'') AS expenditure,-- 支出 IFNULL(sum(dsd.total_amount)-sum(dsd.apply_money),'') AS profit, -- 利润 h.id AS houseId -- 房子id FROM dj_deliver_split_deliver dsd -- 后台生成发货单 INNER JOIN dj_house h ON dsd.house_id = h.id -- 房子 INNER JOIN dj_member m ON m.id = h.member_id -- 工人 INNER JOIN dj_deliver_order_split_item dosi ON dsd.id = dosi.split_deliver_id -- 要货单明细 WHERE dsd.storefront_id = #{storefrontId}
AND ( dsd.ship_address LIKE CONCAT('%',#{searchKey},'%') or m.NAME LIKE CONCAT('%',#{searchKey},'%') )
and dsd.city_id=#{cityId} GROUP BY h.id

select tt.storefrontId,tt.shipAddress,tt.name,tt.mobile,sum(tt.income) income,sum(tt.expenditure) expenditure,sum(profit) profit,houseId,addressId,supId from ( SELECT dsd.storefront_id AS storefrontId, m.address AS shipAddress, m.`name`, m.mobile, sum(IFNULL(dsd.total_amount, 0)) AS income, sum(IFNULL(dsd.apply_money, 0)) AS expenditure, sum(IFNULL(dsd.total_amount, 0)) - sum(IFNULL(dsd.apply_money, 0)) AS profit, m.house_id houseId, m.id addressId, dsd.supplier_id supId, dsd.create_date FROM dj_deliver_split_deliver dsd INNER JOIN dj_member_address m ON (dsd.address_id = m.id OR dsd.house_id = m.house_id) WHERE dsd.storefront_id = #{storefrontId}
AND dsd.supplier_id = #{supId}
AND ( m.address LIKE CONCAT('%',#{searchKey},'%') or m.name LIKE CONCAT('%',#{searchKey},'%') or m.mobile LIKE CONCAT('%',#{searchKey},'%') )
GROUP BY m.id,m.house_id union all SELECT dsd.storefront_id AS storefrontId, m.address AS shipAddress, m.`name`, m.mobile, sum(IFNULL(dsd.apply_money, 0)) AS income, sum(IFNULL(dsd.total_amount, 0)) AS expenditure, (sum(IFNULL(dsd.apply_money, 0))- sum(IFNULL(dsd.total_amount, 0))) AS profit, m.house_id houseId, m.id addressId, dsd.supplier_id supId, dsd.create_date FROM dj_repair_mend_deliver dsd INNER JOIN dj_member_address m ON (dsd.address_id = m.id OR dsd.house_id = m.house_id) WHERE dsd.storefront_id = #{storefrontId}
AND dsd.supplier_id = #{supId}
AND ( m.address LIKE CONCAT('%',#{searchKey},'%') or m.name LIKE CONCAT('%',#{searchKey},'%') or m.mobile LIKE CONCAT('%',#{searchKey},'%') )
GROUP BY m.id,m.house_id ) tt group by tt.addressId,tt.houseId ORDER BY tt.create_date DESC
