mapper.select:
SELECT IFNULL(sum(tt.total_price),0) totalPrice FROM ( SELECT bm.id, bm.product_id, pt.category_id, bm.price, bm.shop_count, bm.total_price FROM dj_actuary_budget_material bm, dj_basics_storefront_product sp, dj_basics_product_template pt, dj_pay_business_order t, dj_deliver_order t1 WHERE t.house_id = #{houseId} and bm.product_id=sp.id and sp.prod_template_id=pt.id AND bm.house_id = t.house_id AND t.number = t1.business_order_number AND t.state = 3
and bm.worker_type_id = #{workerTypeId}
and bm.steta = #{steta}
and pt.category_id in(select id from dj_basics_goods_category bgc where bgc.parent_top=#{categoryTopId})
GROUP BY bm.id, bm.product_id, bm.shop_count, bm.total_price ) tt

SELECT bgc.id categoryId, pt.goods_id goodsId, bm.product_id productId, pt.id productTemplateId, bm.product_name productName, pt.product_sn productSn, sp.image image, bm.unit_name unitName, bm.price, bm.shop_count shopCount, bm.total_price actualPaymentPrice, bm.id actuaryBudgetId FROM dj_actuary_budget_material bm, dj_basics_storefront_product sp, dj_basics_product_template pt, dj_basics_goods_category bgc WHERE bm.house_id = #{houseId} AND bm.product_id = sp.id AND sp.prod_template_id = pt.id AND pt.category_id = bgc.id and bgc.id=#{categoryId}
and bm.worker_type_id = #{workerTypeId}
and pt.category_id in(select id from dj_basics_goods_category bgc where bgc.parent_top=#{categoryTopId})
and FIND_IN_SET(#{labelValId},bgc.category_label_ids)
