mapper.select:
SELECT m.categoryId, m.categoryName, sum(actualPaymentPrice) actualPaymentPrice, sum(purchaseTotalPrice) purchaseTotalPrice FROM ( SELECT t5.id categoryId, t5. NAME categoryName, ( IFNULL( t2.actual_payment_price, IFNULL( t2.total_price, t2.price * t2.shop_count ) ) + IFNULL( ( SELECT price FROM dj_deliver_order_added_product tt WHERE tt.any_order_id = t2.id ), 0 ) * t2.shop_count ) actualPaymentPrice, 0 purchaseTotalPrice FROM dj_deliver_order t1, dj_deliver_order_item t2, dj_basics_storefront_product t3, dj_basics_product_template t4, dj_basics_goods_category t5 WHERE t1.id = t2.order_id AND t2.product_id = t3.id AND t3.prod_template_id = t4.id AND t4.category_id = t5.id AND t1.house_id = #{houseId}
and FIND_IN_SET(#{labelValId} ,t5.category_label_ids)
UNION ALL SELECT t3.id categoryId, t3.`name` categoryName, IFNULL(t1.total_price, 0) actualPaymentPrice, IFNULL(t1.total_price, 0) purchaseTotalPrice FROM dj_actuary_budget_material t1, dj_basics_goods t2, dj_basics_goods_category t3 WHERE t1.steta = 2 AND t1.goods_id = t2.id AND t2.category_id = t3.id AND t1.delete_state != 1 AND t1.product_id IS NULL AND t1.house_id = #{houseId}
and FIND_IN_SET(#{labelValId} ,t3.category_label_ids)
AND EXISTS ( SELECT 1 FROM dj_deliver_order t4 WHERE t1.house_id = t4.house_id AND t1.worker_type_id = t4.worker_type_id ) ) m group by m.categoryId, m.categoryName

SELECT t5.category_id categoryId, t2.id orderItemId, t4.goods_id goodsId, t5.name goodsName, t2.product_id productId, t4.id productTemplateId, t2.product_name productName, t4.product_sn productSn, t4.image, t2.price price, t2.shop_count shopCount, (IFNULL(t2.actual_payment_price,IFNULL(t2.total_price,t2.price*t2.shop_count))+ IFNULL( (SELECT price FROM dj_deliver_order_added_product tt WHERE tt.any_order_id = t2.id),0) * t2.shop_count) actualPaymentPrice, 1 steta, '' actuaryBudgetId, t5.housekeeper_acceptance showHousekeeperAcceptance FROM dj_deliver_order t1, dj_deliver_order_item t2, dj_basics_storefront_product t3, dj_basics_product_template t4, dj_basics_goods t5, dj_basics_goods_category t6 WHERE t1.id = t2.order_id and t2.product_id=t3.id and t3.prod_template_id=t4.id and t4.goods_id=t5.id and t5.category_id = t6.id and t1.house_id=#{houseId} and t5.category_id=#{categoryId}
and FIND_IN_SET(#{labelValId} ,t6.category_label_ids)
union all SELECT t2.category_id categoryId, '' orderItemId, t2.id goodsId, t2.name goodsName, '' productId, '' productTemplateId, t2.name productName, '' productSn, '' image, t1.price, t1.shop_count shopCount, t1.total_price actualPaymentPrice, 2 steta, t1.id actuaryBudgetId, t2.housekeeper_acceptance showHousekeeperAcceptance FROM dj_actuary_budget_material t1, dj_basics_goods t2, dj_basics_goods_category t3 WHERE t1.steta = 2 AND t1.goods_id = t2.id AND t2.category_id = t3.id AND t1.delete_state != 1 AND t1.product_id IS NULL AND t1.house_id = #{houseId} and t2.category_id=#{categoryId}
and FIND_IN_SET(#{labelValId} ,t3.category_label_ids)
AND EXISTS ( SELECT 1 FROM dj_deliver_order t4 WHERE t1.house_id = t4.house_id AND t1.worker_type_id = t4.worker_type_id )

SELECT ddo.id, IFNULL(ddo.house_id,'') as houseId, IFNULL(ddo.business_order_number,'') as businessOrderNumber, IFNULL(ddo.total_amount,'') as totalAmount, IFNULL(ddo.member_id,'') as memberId, IFNULL(ddo.address_id,'') as addressId, IFNULL(ddo.worker_id,'') as workerId, IFNULL(ddo.worker_type_name,'') as workerTypeName, IFNULL(ddo.worker_type_id,'') as workerTypeId, IFNULL(ddo.style_name,'') as styleName, IFNULL(ddo.style_price,'') as stylePrice, IFNULL(ddo.budget_cost,'') as budgetCost, ddo.type, ddo.payment, IFNULL(dbs.system_logo,'') as storefrontIcon, IFNULL(ddo.storefont_id,'') as storefrontId, IFNULL(ddo.city_id,'') as cityId, IFNULL(ddo.total_discount_price,'') as totalDiscountPrice, IFNULL(ddo.total_stevedorage_cost,'') as totalStevedorageCost, IFNULL(ddo.total_transportation_cost,'') as totalTransportationCost, IFNULL(ddo.order_type,'') as orderType, IFNULL(ddo.actual_payment_price,'') as actualPaymentPrice, IFNULL(ddo.is_pay_money,'') as isPayMoney, IFNULL(ddo.is_show_order,'') as isShowOrder, IFNULL(ddo.order_status,'') as orderStatus, IFNULL(ddo.order_generation_time,null ) as orderGenerationTime, IFNULL(ddo.order_source,'') as orderSource, IFNULL(ddo.create_date,null) as createDate, IFNULL(ddo.modify_date,null) as modifyDate, IFNULL(ddo.data_status,'') as dataStatus, IFNULL(dbs.storefront_name,'') as storefrontName from dj_deliver_order ddo left join dj_basics_storefront dbs on ddo.storefont_id=dbs.id where ddo.house_id = #{houseId}
and ddo.city_id = #{cityId}
and ddo.order_status = #{orderStatus}

select IFNULL(ddsd.id,'') as id, IFNULL(dbs.storefront_name,'') as storefrontName,-- 店铺名称 IFNULL(ddsd.create_date,'') as createDate,-- 创建时间 IFNULL(ddsd.number,'') as number,-- 订单号 IFNULL(ddsd.shipping_state,'') as shippingState,-- 订单状态 IFNULL(ddsd.total_amount,'') as totalAmount,-- 总价格 '' as total-- 件数 from dj_deliver_split_deliver ddsd inner join dj_basics_storefront dbs on dbs.id=ddsd.storefront_id where ddsd.house_id=#{houseId}
and ddsd.city_id = #{cityId}
and ddsd.shipping_state = #{orderStatus}

select IFNULL(dos.id,'') as id, IFNULL(dbs.storefront_name,'') as storefrontName,-- 店铺名称 dbs.storefront_logo as StorefrontIcon, dbs.storefront_type as storefrontType, dos.house_id as houseId, IFNULL(dos.create_date,'') as createDate,-- 创建时间 IFNULL(dos.number,'') as orderNumber,-- 订单号 IFNULL(dos.total_amount,'') as totalAmount,-- 总价格 dos.city_id as cityId, dosi.order_split_id as orderSplitId, dosi.image as image, dosi.product_name as productName, dosi.price as price, dosi.num as num from dj_deliver_order_split dos LEFT JOIN dj_deliver_order_split_item dosi on dos.id = dosi.order_split_id LEFT JOIN dj_deliver_split_deliver ddsd on dosi.split_deliver_id = ddsd.id inner join dj_basics_storefront dbs on dbs.id=ddsd.storefront_id where dos.house_id = #{houseId} and dos.data_status = 0 and dos.apply_status = 2
and dos.city_id = #{cityId}
and ( (dosi.split_deliver_id is null or dosi.split_deliver_id = '') or (dosi.split_deliver_id is not null and ddsd.shipping_state = 0) )
