mapper.select:
SELECT t1.id repairWorkOrderId, t1.title_name repairWorkOrderName, t1.create_date applyDate, t1.house_id houseId, CONCAT(t1.content_a,t1.content_b) content, t1.worker_type_id workTypeId, t1.state, t2.`name` workTypeName, t3.worker_id workerId , t4.`name` workerName, t4.mobile workerMobile FROM dj_repair_change_order t1, dj_core_worker_type t2, dj_core_house_worker_order t3 , dj_member t4 where t1.type=#{type} and t1.worker_type_id=t2.id and t1.house_id=t3.house_id and t1.worker_type_id=t3.worker_type_id and t3.worker_id = t4.id and t1.house_id=#{houseId} and t1.data_status = '0' and t1.type=#{type}
and t1.worker_id=#{memberId}
and t1.member_id=#{memberId}
order by t1.create_date desc
