mapper.select:
SELECT t1.storefont_id storefrontId, t1.id orderId, t1.address_id addressId, t1.house_id houseId, t1.business_order_number businessOrderNumber, t1.order_number orderNumber, t4.storefront_name storefrontName FROM dj_deliver_order t1, dj_pay_business_order t3, dj_basics_storefront t4, dj_member_address ma WHERE t1.business_order_number = t3.number and t1.storefont_id=t4.id AND t1.payment IS NOT NULL AND t3.state = 3 and (t1.house_id=ma.house_id or t1.address_id=ma.id) and ma.member_id=#{memberId}
AND t1.house_id = #{houseId}
and exists (select 1 from dj_deliver_order_item t2 where t1.id=t2.order_id AND t2.shop_count > (IFNULL(t2.ask_count, 0)+IFNULL(t2.return_count,0)) AND t2.data_status = 0 and t2.product_type in (0,1,3)
AND (t2.product_name LIKE CONCAT('%',#{searchKey},'%') or t1.order_number LIKE CONCAT('%',#{searchKey},'%')) )

SELECT t.id repairMendOrderId, t.storefront_id storefrontId, t1.storefront_name storefrontName, t1.system_logo storefrontIcon, t1.mobile storefrontMobile, t.house_id houseId, t.number repairOrderNum, IFNULL(t.total_stevedorage_cost,0) totalStevedorageCost, IFNULL(t.carriage,0) carriage, t.create_date applyDate, t.apply_member_id applyMemberId, t2.`name` applyMemberName, IFNULL(t.actual_total_amount,t.total_amount+IFNULL(t.carriage,0)) actualTotalAmount, t.total_amount totalAmount, t.state, t.type, t.image_arr imageArr FROM dj_repair_mend_order t, dj_basics_storefront t1, dj_member t2 where t.storefront_id=t1.id and t.apply_member_id=t2.id and t.house_id=#{houseId}
and t.type in(4,6)
and t.type=#{type}
and t.state<>0 order by t.create_date desc
