mapper.select:
SELECT t.id shellOrderId, t.number, t.product_id productId, t.exchange_client exchangeClient, t.exchange_time exchangeTime, t.member_id memberId, t.member_name memberName, t.member_mobile memberMobile, t.`status`, t.address_id addressId, t.address, t.exchange_num exchangeNum, t.integral, t.money, t.image, p. NAME productName, p.product_type productType FROM dj_home_shell_order t, dj_home_shell_product p WHERE t.data_status=0 and t.product_id = p.id and t.status in(1,2,3,4,5)
and t.exchange_client=#{exchangeClient}
and t.status=#{status}
and t.exchange_time>=#{startTime}
and t.exchange_time<DATE_ADD(#{exchangeClient},INTERVAL 1 DAY)
AND (t.number LIKE CONCAT('%',#{searchKey},'%') or t.member_name LIKE CONCAT('%',#{searchKey},'%') or t.member_mobile LIKE CONCAT('%',#{searchKey},'%'))
order by t.exchange_time desc
