mapper.select:
SELECT id, user_id as userId, name, worker_type_name as workerTypeName, worker_type_id as workerTypeId, style_type as styleType, number_of_use as numberOfUse, applicable_area as applicableArea, state_type as stateType, create_date as createDate, modify_date as modifyDate FROM dj_actuary_actuarial_template where 1=1
AND worker_type_id=#{workerTypeId}
AND state_type=#{stateType}
AND name LIKE CONCAT('%',#{name},'%')
ORDER BY create_date desc

mapper.update:
UPDATE dj_actuary_actuarial_template
number_of_use=number_of_use+1, modify_date=now()
WHERE id=#{id}
