mapper.select:
( SELECT house_id houseId, worker_type_id workerTypeId, '0' type, SUM(total_price) totalAmount FROM `dj_actuary_budget_material` WHERE delete_state != 1
AND `worker_type_id` = #{workerTypeId}
AND `house_id` = #{houseId} GROUP BY houseId, workerTypeId ) UNION ALL ( SELECT house_id houseId, worker_type_id workerTypeId, '2' type, SUM(total_price) totalAmount FROM `dj_actuary_budget_worker` WHERE delete_state != 1
AND `worker_type_id` = #{workerTypeId}
AND `house_id` = #{houseId} GROUP BY houseId, workerTypeId );

SELECT

FROM dj_actuary_budget_material
WHERE
house_id=#{houseId}

	and category_id = #{categoryId}

and delete_state in (0,2,3,4)

		select DISTINCT bm.category_id
		from dj_actuary_budget_material bm
 		where bm.house_id = #{houseId}
 		and bm.delete_state in (0,2,3,4)
 		and bm.category_id !=''
 		and bm.category_id is not null;

SELECT
FROM dj_actuary_budget_material where steta in (1,2) and delete_state != 1 and house_id = #{houseId}
and product_name LIKE CONCAT('%',#{productName},'%')
and product_type = #{productType}
and worker_type_id = #{workerTypeId}
and category_id = #{categoryId}
GROUP BY product_id ORDER BY create_date,house_id,product_sn desc

SELECT

FROM dj_actuary_budget_material
WHERE
house_id=#{houseId}
and worker_type_id =#{workerTypeId}
and goods_id =#{goodsId}
and product_type = 0
and delete_state in (0,2,4)
ORDER BY category_id,product_sn,goods_name desc

SELECT

FROM dj_actuary_budget_material
WHERE
house_id=#{houseId}
and worker_type_id =#{workerTypeId}
and goods_id =#{goodsId}
and product_type = #{productType}
and delete_state = 5
ORDER BY category_id,product_sn,goods_name desc

SELECT

FROM dj_actuary_budget_material
where house_id=#{houseId}
and worker_type_id =#{workerTypeId}
and delete_state!=1
ORDER BY category_id,product_sn,goods_name desc

SELECT

FROM dj_actuary_budget_material
where house_id=#{houseId}
and worker_type_id =#{workerTypeId}
and delete_state!=1
ORDER BY category_id,product_sn,goods_name desc

mapper.delete:
DELETE FROM dj_actuary_budget_material
WHERE house_id=#{houseId}
and worker_type_id =#{workerTypeId}
