mapper.select:
SELECT id, config_name configName, config_type configType, service_type_id serviceTypeId, #{addressUrl} addressUrl FROM dj_simulation_template_config t where data_status = '0' and city_id = #{cityId} and exists (select 1 from dj_simulation_template_config_detail t1 where t.id=t1.simulation_template_id and t1.config_status = 0)
and id=#{id}
and service_type_id=#{serviceTypeId}
order by config_type,create_date

delete from dj_simulation_template_config
where id not in(select distinct simulation_template_id from dj_simulation_template_config_detail)
and city_id=#{cityId}
and service_type_id = #{serviceTypeId}
