mapper.select:
SELECT id, config_name configName, config_type configType, config_detail configDetail FROM dj_actuarial_template_config ac where ac.config_type in(1,2) and city_id=#{cityId}
and ac.service_type_id=#{serviceTypeId}
and ac.id=#{id}

SELECT id, config_name configName, config_type configType, config_detail configDetail FROM dj_actuarial_template_config ac where ac.config_type in(1,2) and ac.city_id=#{cityId} and service_type_id = #{serviceTypeId}
ac.config_type =#{configType}
order by configType
