mapper.select:
SELECT

FROM dj_basics_goods_category where parent_id =#{parentId}
ORDER BY sort,create_date asc

SELECT

FROM dj_basics_goods_category where name =#{name}
ORDER BY sort,create_date asc

SELECT p.id AS id, p.image AS image, p.price AS price, u. NAME AS unitName, g.type AS goodsType, 0 AS type, p. NAME AS NAME FROM dj_basics_product_template p, dj_basics_goods_category c, dj_basics_goods g, dj_basics_unit u WHERE c.id = p.category_id AND p.goods_id=g.id and g.category_id=c.id AND u.id = p.convert_unit AND p.maket = 1 AND p.type = 1 and p.maket = 1 AND g.type <4
AND c.parent_top = #{categoryId}
ORDER BY p.istop DESC, p.create_date DESC
