mapper.select:
SELECT id, name, state, create_date as createDate, modify_date as modifyDate from dj_basics_goods_group where 1=1
and name =#{name}
ORDER BY create_date desc

mapper.update:
UPDATE dj_basics_group_link
SET state=#{state}，modify_date=#{modifyDate}
where group_id=#{groupId}
and  product_id=#{productId}
