mapper.update:
UPDATE dj_basics_group_link
SET state=#{state},modify_date=#{modifyDate}
where group_id=#{groupId}
and  product_id=#{productId}

mapper.select:
SELECT

FROM dj_basics_group_link
where group_id=#{groupId}
and product_id=#{productId}
ORDER BY create_date desc

SELECT

FROM dj_basics_group_link
where group_id=#{groupId}
and product_id=#{productId}
ORDER BY create_date desc

SELECT

FROM dj_basics_group_link
where 1=1
and product_id=#{productId}
and goods_id=#{goodsId}
ORDER BY create_date desc
