mapper.select:
SELECT id, name, brand_id as brandId, content, create_date as createDate, modify_date as modifyDate FROM dj_basics_brand_series where id in( select series_id from dj_basics_goods_series where 1=1
and goods_id=#{goodsId}
and brand_id=#{brandId}
) ORDER BY create_date desc

SELECT

FROM dj_basics_goods
where 1=1

	and category_id=#{categoryId}


	and name=#{name}

ORDER BY create_date desc

SELECT

FROM dj_basics_goods as g
where 1=1


	and category_id=#{categoryId}


	and type =#{type}


	and buy !=#{buy}


	and (SELECT COUNT(*) > 0 as num FROM dj_basics_product p WHERE p.goods_id = g.id and p.type =1)


	and name LIKE CONCAT('%',#{name},'%')

ORDER BY create_date desc
