mapper.select:
SELECT FROM dj_basics_product
where category_id=#{category_id}
ORDER BY create_date desc

SELECT FROM dj_basics_product where data_status = 0
and name=#{name}
ORDER BY create_date desc

SELECT FROM dj_basics_product where data_status = 0
and product_sn=#{productSn}
ORDER BY create_date desc

select p.id as id, p.name as name, p.goods_id as goodsId, p.category_id as categoryId, p.product_sn as productSn, p.image as image, p.unit_id as unitId, p.label_id as labelId, p.unit_name as unitName, p.type as type, p.price as price, p.brand_id as brandId, p.brand_series_id as brandSeriesId, p.attribute_id_arr AS attributeIdArr, p.value_name_arr AS valueNameArr, p.value_id_arr AS valueIdArr from dj_basics_product p where type=1 and maket=1
and p.goods_id=#{goodsId}
and p.brand_id=#{brandId}
and p.brand_series_id=#{brandSeriesId}
and FIND_IN_SET(#{valueId},p.value_id_arr) ;

select p.id, p.NAME, p.goods_id AS goodsId, p.category_id AS categoryId, p.product_sn AS productSn, p.image, p.unit_id as unitId, p.label_id as labelId, p.unit_name as unitName, p.weight, p.type, p.maket, p.cost, p.price, p.profit, p.brand_id AS brandId, p.brand_series_id AS brandSeriesId, p.attribute_id_arr AS attributeIdArr, p.value_name_arr AS valueNameArr, p.value_id_arr AS valueIdArr, p.convert_unit AS convertUnit, p.convert_quality AS convertQuality, p.create_date AS createDate, p.modify_date AS modifyDate from dj_actuary_budget_material abm ,dj_basics_product p WHERE abm.product_id =p.id and abm.delete_state!=1 and abm.steta=1 and abm.house_id =#{houseId}
AND abm.product_name LIKE CONCAT('%',#{name},'%')
AND p.category_id =#{categoryId}

select
from dj_basics_product p where type=1 and maket=1 and p.brand_series_id=#{brandSeriesId}
and FIND_IN_SET(#{valueId},p.value_id_arr) ;
