mapper.select:
SELECT

FROM dj_basics_technology
where 1=1 and city_id = #{cityId}

    and
    worker_type_id=#{workerTypeId}


    and
    name LIKE CONCAT('%',#{name},'%')


    and
    material_or_worker=#{materialOrWorker}

ORDER BY create_date desc

SELECT
FROM dj_basics_technology where 1=1 and city_id = #{cityId}
and worker_type_id=#{workerTypeId}
and name LIKE CONCAT('%',#{name},'%')
and (goods_id = #{goodsId} or FIND_IN_SET(id,#{goodsId}))
and material_or_worker=#{materialOrWorker}
ORDER BY create_date desc

SELECT
FROM dj_basics_technology where 1=1 and city_id = #{cityId}
and name LIKE CONCAT('%',#{name},'%')
and worker_type_id=#{workerTypeId}
GROUP BY `name` ORDER BY create_date desc
