mapper.select:
SELECT id, name, worker_goods_sn as workerGoodsSn, image, unit_id as unitId, unit_name as unitName, price, istop, other_name as otherName, sales, work_explain as workExplain, worker_dec as workerDec, worker_standard as workerStandard, worker_type_id as workerTypeId, show_goods as showGoods, create_date as createDate, modify_date as modifyDate, last_price lastPrice, last_time lastTime, technology_ids technologyIds, considerations, calculate_content calculateContent, build_content buildContent from dj_basics_worker_goods where data_status = 0
AND worker_type_id=#{workerTypeId}
AND (worker_goods_sn like CONCAT('%',#{searchKey},'%') OR name like CONCAT('%',#{searchKey},'%') OR other_name like CONCAT('%',#{searchKey},'%') )
AND istop=#{istops}
AND show_goods=#{showGoods}
ORDER BY istop DESC,create_date desc

SELECT id, name, worker_goods_sn as workerGoodsSn, image, unit_id as unitId, unit_name as unitName, price, istop, other_name as otherName, sales, work_explain as workExplain, worker_dec as workerDec, worker_standard as workerStandard, worker_type_id as workerTypeId, show_goods as showGoods, create_date as createDate, modify_date as modifyDate from dj_basics_worker_goods as w where w.data_status = 0
and w.worker_goods_sn =#{workerGoodsSn}
AND w.worker_type_id =#{workerTypeId}
ORDER BY istop DESC,create_date desc

SELECT id, name, worker_goods_sn as workerGoodsSn, image, unit_id as unitId, unit_name as unitName, price, istop, other_name as otherName, sales, work_explain as workExplain, worker_dec as workerDec, worker_standard as workerStandard, worker_type_id as workerTypeId, show_goods as showGoods, create_date as createDate, modify_date as modifyDate from dj_basics_worker_goods as w where w.data_status = 0
and w.name =#{name}
AND w.worker_type_id =#{workerTypeId}
ORDER BY istop DESC,create_date desc

select w.id, w.name, w.worker_goods_sn as workerGoodsSn, w.image, w.unit_id as unitId, w.unit_name as unitName, w.price, istop, w.sales, w.work_explain as workExplain, w.worker_dec as workerDec, w.worker_standard as workerStandard, w.worker_type_id as workerTypeId, w.show_goods as showGoods, w.create_date as createDate, w.modify_date as modifyDate from dj_actuary_budget_worker abw ,dj_basics_worker_goods w WHERE abw.worker_goods_id =w.id and abw.delete_state!=1 and abw.steta=1 and abw.house_id =#{houseId}
AND abw.name LIKE CONCAT('%',#{name},'%')
AND w.worker_type_id =#{workerTypeId}

mapper.update:
update dj_basics_worker_goods
set unit_name=#{unitName}
where unit_id=#{unitId}
