mapper.select:
SELECT

FROM dj_basics_goods_category where name =#{name}
ORDER BY sort,create_date asc

SELECT p.id AS id, p.image AS image, p.price AS price, u. NAME AS unitName, g.type AS goodsType, 0 AS type, p. NAME AS NAME FROM dj_basics_product_template p, dj_basics_goods_category c, dj_basics_goods g, dj_basics_unit u WHERE c.id = p.category_id AND p.goods_id=g.id and g.category_id=c.id AND u.id = p.convert_unit AND p.maket = 1 AND p.type = 1 and p.maket = 1 AND p.type = 1
AND c.parent_top = #{categoryId}
ORDER BY p.istop DESC, p.create_date DESC

SELECT t.id, t.id categoryId, t.NAME categoryName, #{goodsName} goodsName, #{cityId} cityId FROM dj_basics_goods_category t WHERE is_last_category = 1 and parent_id= #{categoryId}
and exists (select 1 from dj_basics_goods t1 where t.id = t1.category_id and t1.name like CONCAT('%',#{goodsName},'%'))
ORDER BY sort,create_date asc

SELECT id, id goodsId, NAME goodsName, category_id categoryId, type, buy, sales FROM dj_basics_goods where category_id= #{id} and city_id=#{cityId}
and name like CONCAT('%',#{goodsName},'%')
order by create_date desc
