mapper.select:
SELECT

FROM dj_basics_goods_category where name =#{name}
ORDER BY create_date desc

SELECT p.id AS id, p.image AS image, p.price AS price, u.name AS unitName, g.type AS goodsType, 0 AS type, p.NAME AS NAME FROM dj_basics_product p INNER JOIN dj_basics_goods_category c ON c.id = p.category_id INNER JOIN dj_basics_goods g ON g.id = p.goods_id INNER JOIN dj_basics_unit u ON u.id = p.convert_unit WHERE p.maket = 1 AND p.type = 1
AND c.parent_top = #{categoryId}
ORDER BY p.istop DESC, p.create_date DESC
