mapper.select:
SELECT

FROM dj_basics_goods

    where
    category_id=#{categoryId}
    and city_id=#{cityId}

ORDER BY create_date desc

SELECT

FROM dj_basics_goods
where category_id=#{categoryId}

    and city_id=#{cityId}

ORDER BY create_date desc

SELECT id, NAME, category_id as categoryId, type, buy, sales, other_name AS otherName, unit_id as unitId, create_date, modify_date, is_influe_decoration_progress as isInflueDecorationProgress, irreversible_reasons as irreversibleReasons, istop, brand_id as brandId, is_elevator_fee as isElevatorFee, indicative_price as indicativePrice, label_ids as labelIds, is_reservation_deliver as isReservationDeliver, attribute_id_arr attributeIdArr FROM dj_basics_goods bg where 1=1 and city_id=#{cityId}
and bg.category_id=#{categoryId}
AND ( bp.`name` LIKE CONCAT('%',#{name}, '%' ) OR bg.`other_name` LIKE CONCAT( '%', #{name}, '%' ) or exists (select 1 from dj_basics_product_template t1 where t1.name like CONCAT('%',#{name}, '%')) )
GROUP BY bg.id
