mapper.select:
select p.id,p.name,p.goods_id,p.category_id,p.product_sn,p.image, p.unit_name,p.unit_id,p.label_id,p.type,p.maket,p.price,p.other_name, p.istop,p.remark ,m.marketing_name,b.name as attributeIdArr from dj_basics_product p left join dj_basics_product_material m on p.id=m.product_id left join dj_basics_attribute b on b.id=m.value_id_arr
and p.product_sn=#{productSn}
ORDER BY p.istop DESC,p.create_date desc

select p.id,p.name,p.goods_id,p.category_id,p.product_sn,p.image, p.unit_name,p.unit_id,p.label_id,p.type,p.maket,p.price,p.other_name, p.istop,p.remark,'' as marketing_name ,'' as attributeIdArr from dj_basics_product p left join dj_basics_product_worker w on p.id=w.product_id
and p.product_sn=#{productSn}
ORDER BY p.istop DESC,p.create_date desc

SELECT

FROM dj_basics_product
where goods_id=#{goodsId}
and  data_status=#{dataStatus}
ORDER BY istop DESC,create_date desc
limit 1
