mapper.select:
SELECT * FROM dj_sup_supplier where 1=1
and name=#{name}
ORDER BY create_date desc

SELECT * FROM dj_sup_supplier where 1=1
and telephone=#{telephone}
ORDER BY create_date desc

mapper.update:
update dj_sup_supplier set
name =

	when #{supplier.id} then #{supplier.name}

,address =

	when #{supplier.id} then #{supplier.address}

,telephone =

	when #{supplier.id} then #{supplier.telephone}

,checkPeople =

	when #{supplier.id} then #{supplier.checkPeople}

,gender =

	when #{supplier.id} then #{supplier.gender}

,email =

	when #{supplier.id} then #{supplier.email}

,notice =

	when #{supplier.id} then #{supplier.notice}

,supplier_level =

	when #{supplier.id} then #{supplier.supplier_level}

,state =

	when #{supplier.id} then #{supplier.state}

,modify_date=SYSDATE()
where id in

	#{supplier.id}

mapper.select:
select p.id, p.NAME, p.goods_id AS goodsId, p.category_id AS categoryId, p.product_sn AS productSn, p.image, p.unit_name as unitName, p.unit_id as unitId, p.weight, p.type, p.maket, p.cost, p.price, p.profit, p.brand_id AS brandId, p.brand_series_id AS brandSeriesId, p.attribute_id_arr AS attributeIdArr, p.value_name_arr AS valueNameArr, p.value_id_arr AS valueIdArr, p.convert_unit AS convertUnit, p.convert_quality AS convertQuality, p.create_date AS createDate, p.modify_date AS modifyDate from dj_basics_product p INNER JOIN dj_sup_supplier_product sp ON p.id = sp.product_id where p.data_status=0
and sp.supplier_id=#{supplierId}
and sp.is_supply=#{isSupply}
and p.name like CONCAT('%',#{likeProductName},'%')
ORDER BY p.product_sn and p.create_date desc
