mapper.select:
select 
from dj_sup_supplier_product
where
    product_id = #{productId}
and
    supplier_id = #{supplierId}

select sp.id, sp.product_id AS productId, sp.supplier_id AS supplierId, sp.price, sp.stock, sp.is_supply AS isSupply, sp.goods_id AS goodsId, sp.create_date AS createDate, sp.modify_date AS modifyDate, sp.data_status AS dataStatus FROM dj_sup_supplier_product sp INNER JOIN dj_basics_product p ON sp.product_id=p.id WHERE sp.is_supply = 1
and sp.product_id =#{productId}
and sp.supplier_id =#{supplierId}
ORDER BY p.category_id DESC
