mapper.select:
SELECT rpr.*, rp.name, rp.from_object, rp.from_object_type, rp.start_date, rp.end_date, rp.city_id, rp.num, rp.surplus_nums, rp.receive_count, rp.is_share, rp.type, rp.remake, r.num rnum, r.money rmoney, r.satisfy_money rsatisfy_money FROM `dj_activity_red_pack_record` rpr INNER JOIN dj_activity_red_pack rp ON rp.id = rpr.red_pack_id INNER JOIN dj_activity_red_pack_rule r ON r.id = rpr.red_pack_rule_id WHERE rpr.data_status=0 and rp.data_status=0 AND rp.delete_state=0
AND rpr.red_pack_id=#{redPackId}
AND rpr.red_pack_rule_id=#{redPackRuleId}
AND rpr.member_id=#{memberId}
AND rp.city_id=#{cityId}
AND rpr.have_receive=#{haveReceive}
AND rpr.have_receive!=0 AND rpr.have_receive!=1
AND rp.end_date >=#{endDate}
order by rpr.have_receive DESC, rpr.create_date

SELECT r.id, p.`name`, p.from_object_type fromObjectType, p.from_object fromObject, p.storefront_id storefrontId, p.from_object_name fromObjectName, IFNULL(p.satisfy_money,0) satisfyMoney, p.type, ( CASE WHEN ( (p.type = 0 || p.type = 1) AND p.satisfy_money > 0 ) THEN CONCAT('满',p.satisfy_money,'元可用') ELSE '无门槛' END ) satisfyMoneyRemark, ( CASE WHEN p.source_type = 1 THEN '平台券' WHEN p.source_type = 2 THEN '店铺券' END ) typeName,
( CASE WHEN r.business_order_number!=null THEN '已使用' else '已失效' END ) statusName,
p.satisfy_money, p.money, p.source_type sourceType, c.`name` cityName, p.start_date startDate, p.end_date endDate FROM dj_activity_red_pack p, dj_activity_red_pack_record r, dj_other_city c WHERE r.red_pack_id = p.id AND p.city_id = c.id
AND p.end_date >= DATE_FORMAT(NOW(), '%Y-%m-%d') and r.business_order_number is null
AND( p.end_date < DATE_FORMAT(NOW(), '%Y-%m-%d') or r.business_order_number is not null)
AND p.start_date <= DATE_FORMAT(NOW(), '%Y-%m-%d') AND p.end_date >= DATE_FORMAT(NOW(), '%Y-%m-%d') and r.business_order_number is null
AND r.member_id = #{memberId}
and p.source_type=#{sourceType}
and r.id=#{activityRedPackId}
order by r.modify_date desc
