mapper.select:
SELECT a.id AS productId, c.`name` AS goodsName, a.product_name AS productName, b.product_sn AS productSn, a.image, a.sell_price AS sellPrice, a.supplied_num AS inventory FROM dj_basics_storefront_product a, dj_basics_product_template b, dj_basics_goods c WHERE a.storefront_id =#{storefrontId} AND a.prod_template_id = b.id AND b.goods_id = c.id AND NOT EXISTS ( SELECT 1 FROM dj_store_activity_product d, dj_store_participate_activities e WHERE d.store_participate_activities_id = e.id AND a.id = d.product_id AND e.store_activity_id =#{storeActivityId}
AND e.activity_session_id =#{activitySessionId}
AND e.storefront_id=#{storefrontId} )

SELECT COUNT(*) FROM dj_activity_session WHERE id =#{id} AND NOT ( end_session < ( SELECT activity_start_time FROM dj_store_activity WHERE id = #{storeActivityId} ) OR session_start_time > ( SELECT end_time FROM dj_store_activity WHERE id = #{storeActivityId} )
OR end_session < ( SELECT session_start_time FROM dj_activity_session WHERE id = #{id1} ) OR session_start_time > ( SELECT end_session FROM dj_activity_session WHERE id = #{id1} )
)

SELECT COUNT(*) FROM dj_store_activity WHERE id = #{id} AND NOT ( end_time < ( SELECT activity_start_time FROM dj_store_activity WHERE id = #{id1} ) OR activity_start_time > ( SELECT end_time FROM dj_store_activity WHERE id = #{id1} )
OR end_time < ( SELECT session_start_time FROM dj_activity_session WHERE id = #{activitySessionId} ) OR activity_start_time > ( SELECT end_session FROM dj_activity_session WHERE id = #{activitySessionId} )
)
