end_session < ( SELECT session_start_time FROM dj_activity_session WHERE id = #{id1} ) OR session_start_time > ( SELECT end_session FROM dj_activity_session WHERE id = #{id1} )
)

SELECT COUNT(*) FROM dj_store_activity WHERE id = #{id} AND NOT ( end_time < ( SELECT activity_start_time FROM dj_store_activity WHERE id = #{id1} ) OR activity_start_time > ( SELECT end_time FROM dj_store_activity WHERE id = #{id1} )
OR end_time < ( SELECT session_start_time FROM dj_activity_session WHERE id = #{activitySessionId} ) OR activity_start_time > ( SELECT end_session FROM dj_activity_session WHERE id = #{activitySessionId} )
)
