mapper.select:
select

from dj_clue
WHERE
data_status=0
and phone=#{phone}

    and cus_service=#{userId}


    and store_id=#{storeId}

select

from dj_clue
WHERE
data_status=0
and phone=#{phone}

    and cus_service=#{userId}

SELECT
c.id as clueId, owername as name, phone, c.modify_date as modifyDate, c.cus_service as userId, c.member_id as memberId, c.phase_status as phaseStatus, c.stage
c.id as clueId, m.id as memberId, c.stage, if(m.name is not null,m.nick_name,m.name) as name, m.mobile as phone, m.modify_date as modifyDate, CONCAT(IFNULL(h.residential,'*'),IFNULL(h.building,'*栋'),IFNULL(h.unit,'*单元'),IFNULL(h.number,'*号')) as houseName, c.cus_service as userId, c.phase_status as phaseStatus
FROM dj_clue c
WHERE c.data_status = 0 and c.stage NOT IN (2,3)
and c.cus_service=#{userId}
and c.cus_service in
#{item.userId}
ORDER BY c.modify_date desc
INNER JOIN dj_member m ON m.id=c.member_id INNER JOIN dj_house h ON h.member_id = m.id and h.data_status =0 and h.visit_state =1 WHERE m.data_status = 0 and c.data_status = 0
and c.cus_service=#{userId}
and c.cus_service in
#{item.userId}
ORDER BY h.modify_date desc
INNER JOIN dj_member m ON m.id=c.member_id INNER JOIN dj_house h ON h.member_id = m.id and h.data_status =0 and h.visit_state =3 WHERE m.data_status = 0 and c.data_status = 0
and c.cus_service=#{userId}
and c.cus_service in
#{item.userId}
ORDER BY h.modify_date desc
limit 1

select clueId,mcId,name,phone,modifyDate,createDate,labelIdArr,userName,phaseStatus from ( SELECT c.id as clueId, null as mcId, c.cus_service as userId, c.owername as name, c.phone, c.modify_date AS modifyDate, c.create_date AS createDate, c.label_id AS labelIdArr, u.username as userName, c.phase_status as phaseStatus FROM dj_clue c inner join dj_user u on c.cus_service=u.id WHERE (c.member_id IS NULL or c.member_id='') and c.stage=2 and c.store_id=#{storeId} and c.data_status=0
AND c.cus_service=#{staff}
AND CONCAT(name,phone,address) like CONCAT('%',#{searchKey},'%')
UNION SELECT mc.id as mcId, null as clueId, mc.user_id as userId, if(m.name is not null,m.nick_name,m.name) as name, m.mobile AS phone, mc.modify_date AS modifyDate, mc.create_date AS createDate, mc.label_id_arr AS labelIdArr, u.username as userName, mc.phase_status as phaseStatus FROM dj_member m INNER JOIN dj_member_customer mc ON m.id=mc.member_id inner join dj_user u on mc.user_id=u.id WHERE mc.stage=2 and mc.store_id=#{storeId} and mc.data_status=0
AND mc.user_id=#{staff}
AND CONCAT(m.name,m.mobile) like CONCAT('%',#{searchKey},'%')
) a order by a.modifyDate
desc

select c.owername as owerName, c.phone as phone, c.cus_service as cusService, c.member_id as memberId, h.id as id, h.member_id as memberId, h.city_id as cityId, h.village_id as villageId, h.visit_state as visitState, h.create_date as createDate, mc.label_id_arr as labelIdArr from dj_house h INNER JOIN dj_member_customer mc on h.member_id = mc.member_id INNER JOIN dj_clue c on h.member_id = c.member_id where h.visit_state = 0 and h.is_rob_stats = 0
and c.cus_service = #{userId}
and c.store_id = #{storeId}
and h.is_type = #{type}

select m.name as name, m.nick_name as nickName, m.head as head, h.visit_state as visitState, mc.user_id as userId from dj_member_customer mc inner join dj_house h on mc.member_id = h.member_id inner join dj_member m on m.id = mc.member_id where 1=1
and mc.user_id = #{userId}
and h.member_id = #{memberId}
and (h.visit_state = 1 or h.visit_state = 3)

SELECT COUNT(0) FROM dj_clue c
WHERE c.data_status = 0 and c.stage NOT IN (2,3)
and c.cus_service=#{userId}
and c.cus_service in
#{item.userId}
INNER JOIN dj_member m ON m.id=c.member_id INNER JOIN dj_house h ON h.member_id = m.id and h.data_status =0 and h.visit_state =1 WHERE m.data_status = 0 and c.data_status = 0
and c.cus_service=#{userId}
and c.cus_service in
#{item.userId}
INNER JOIN dj_member m ON m.id=c.member_id INNER JOIN dj_house h ON h.member_id = m.id and h.data_status =0 and h.visit_state =3 WHERE m.data_status = 0 and c.data_status = 0
and c.cus_service=#{userId}
and c.cus_service in
#{item.userId}
and tips = 1

mapper.update:
update dj_house h
set h.is_rob_stats = 1
where h.id = #{id}
