mapper.select:
select

from dj_clue
WHERE
data_status=0
and phone=#{phone}

    and cus_service=#{userId}


    and store_id=#{storeId}

select

from dj_clue
WHERE
data_status=0
and phone=#{phone}

    and cus_service=#{userId}

SELECT c.id as clueId, c.wechat, c.clue_type as clueType, mc.id as mcId, mc.member_id as memberId, h.id as houseId, mc.user_id as userId, CONCAT(IFNULL(h.residential,'*'),IFNULL(CONCAT(h.building,'栋'),'*栋'),IFNULL(CONCAT(h.unit,'单元'),'*单元'),IFNULL(CONCAT(h.number,'号'),'*号')) as houseName, m.mobile as mobile, m.nick_name as name, h.construction_date as createDate, h.completed_date as completedDate, u.username, mc.phase_status as phaseStatus FROM dj_member_customer mc inner join dj_clue c ON mc.member_id=c.member_id and mc.user_id=c.cus_service inner join dj_user u on mc.user_id=u.id INNER JOIN dj_house h ON mc.member_id=h.member_id INNER JOIN dj_already_rob_single ars ON ars.user_id = c.cus_service AND ars.house_id = h.id INNER JOIN dj_member m on m.id=mc.member_id WHERE mc.phase_status=1 and c.phase_status=1 and h.data_status = 0
and mc.user_id=#{userId}
AND mc.store_id=#{storeId}
AND mc.user_id=#{staff}
AND h.visit_state=1
AND (h.visit_state = 3 or h.visit_state=4)
AND( m.nick_name like CONCAT('%',#{searchKey},'%') OR c.phone like CONCAT('%',#{searchKey},'%') OR h.residential like CONCAT('%',#{searchKey},'%') OR CONCAT_WS(IFNULL(h.residential, '*'),IFNULL(CONCAT(h.building, '栋'),'*栋'),IFNULL(CONCAT(h.unit, '单元'),'*单元'),IFNULL(CONCAT(h.number, '号'),'*号')) like CONCAT('%',#{searchKey},'%') )
ORDER BY h.create_date
ORDER BY h.completed_date
desc

select c.phone as phone, c.cus_service as cusService, c.member_id as memberId, c.id as clueId, c.stage as stage, c.phase_status as phaseStatus, h.id as houseId, h.city_id as cityId, h.village_id as villageId, h.visit_state as visitState, h.create_date as createDate, mc.label_id_arr as labelIdArr, mc.id as mcId, m.nick_name as owerName from dj_house h INNER JOIN dj_member_customer mc on h.member_id = mc.member_id INNER JOIN dj_clue c on mc.member_id = c.member_id and mc.user_id = c.cus_service INNER JOIN dj_member m on mc.member_id = m.id where h.visit_state = 0 and h.data_status=0
and h.is_rob_stats = #{isRobStats}
and (c.cus_service = #{userId} or c.store_id = #{storeId})
and c.store_id = #{storeId}
and c.cus_service = #{userId}
union SELECT c.phone as phone, c.cus_service as cusService, c.member_id as memberId, c.id as clueId, c.stage as stage, c.phase_status as phaseStatus, h.id as houseId, h.city_id as cityId, h.village_id as villageId, h.visit_state as visitState, h.create_date as createDate, mc.label_id_arr as labelIdArr, mc.id as mcId, m.nick_name as owerName FROM dj_clue c INNER JOIN dj_house h ON c.member_id = h.member_id INNER JOIN dj_member_customer mc ON mc.member_id = c.member_id INNER JOIN dj_member m on m.id=c.member_id WHERE ( c.cus_service IS NULL OR c.cus_service = "" ) and c.store_id = #{storeId} and h.visit_state = 0 and h.data_status=0 and h.is_rob_stats = 0

select u.username as name, m.head, sum(rm.month_royalty) as monthRoyaltys, sum(rm.month_royalty) as meterRoyaltys, sum(rm.count_arr_royalty) as arrRoyalty FROM dj_user u INNER JOIN dj_royalty_match rm on u.id=rm.user_id INNER JOIN dj_member m on u.member_id=m.id where rm.house_id in
#{item}
and rm.user_id=#{userId}

SELECT COUNT(0) FROM dj_clue c LEFT JOIN dj_member_customer mc ON mc.member_id = c.member_id AND mc.user_id = c.cus_service
WHERE c.data_status = 0 and c.stage NOT IN (2,3,4,5)
and c.cus_service=#{userId}
and c.cus_service in
#{item.userId}
INNER JOIN dj_house h ON h.member_id = mc.member_id and h.data_status =0
and h.visit_state =1
and h.visit_state =3
WHERE mc.data_status = 0 and c.data_status = 0
and c.cus_service=#{userId}
and c.cus_service in
#{item.userId}
and (c.tips = 1 or mc.tips=1)

mapper.update:
update dj_house h
set h.is_rob_stats = #{isRobStats}
where h.id = #{id}

update dj_clue c
set c.cus_service = #{cusService}
where c.id = #{clueId}

update dj_member_customer mc
set mc.user_id = #{userId}
where mc.id = #{mcId}

mapper.select:
SELECT c.id as clueId, c.owername as owerName, c.phone as phone, c.label_id as labelId, c.wechat as wechat, c.create_date as createDate, c.remark as remark, c.phase_status as phaseStatus, c.stage as stage, c.create_date as createDate, c.branch_user as branchUser, m.name as userName, m.head as head, m.id as memberId FROM dj_clue c INNER JOIN dj_user u ON c.cus_service = u.id LEFT JOIN dj_member m ON u.member_id = m.id where 1=1
and c.id = #{id}
and c.cus_service = #{userId}

mapper.update:
update dj_clue
set stage=#{stage}

    ,tips=#{tips}

where 1=1

    and cus_service = #{userId}


    and member_id = #{memberId}

update dj_clue c
set c.store_id=#{storeId}
where c.cus_service=#{userId}
