mapper.select:
SELECT c.id as id, c.member_id as memberId, c.user_name as memberName, c.user_nick_name as memberNickName, c.user_mobile as memberMobile, c.complain_type as complainType, c.operate_id as operateId, c.operate_name as operateName, c.user_id as userId, c.status as status, c.description as description, c.business_id as businessId, c.house_id as houseId, CONCAT( h.residential, IFNULL(h.building, '*'), '栋', IFNULL(CONCAT(h.unit, '单元'),'' ), IFNULL(h.number, '*'), '号' ) as houseName, c.files as files, c.create_date as createDate, c.modify_date as modifyDate FROM dj_complain c LEFT JOIN dj_house h ON c.house_id = h.id WHERE c.data_status = 0
and c.complain_type = #{complainType}
and c.status = #{state}
and CONCAT(c.user_mobile,c.user_name,c.user_nick_name) like CONCAT('%',#{searchKey},'%')
ORDER by c.modify_date desc

select m.name as memberName, m.nick_name as memberNickName, m.mobile as memberMobile, m.id as memberId, mr.complain_type as complainType, mr.create_date as createDate, mr.id as id, mr.handle_type as status, u.username as operateName, u.id as operateId, CONCAT( h.residential, IFNULL(h.building, '*'), '栋', IFNULL(CONCAT(h.unit, '单元'),'' ), IFNULL(h.number, '*'), '号' ) as houseName, h.id as houseId FROM dj_maintenance_record mr LEFT JOIN dj_member m on mr.member_id = m.id LEFT JOIN dj_user u on mr.user_id = u.id LEFT JOIN dj_house h ON mr.house_id = h.id where mr.data_status = 0
and mr.complain_type = #{complainType}
and mr.handle_type = #{state}
and CONCAT(m.mobile,m.name,m.nick_name) like CONCAT('%',#{searchKey},'%')
ORDER by mr.modify_date desc
