mapper.select:
SELECT o.id, IFNULL(o.module_id, #{moduleId}) moduleId, IFNULL(o.batch_code, #{batchCodeTow}) batchCode, o.type_id, r.id rankId, IFNULL(o.rule_field_name, #{fieldName}) ruleFieldName, IFNULL(o.rule_field_value, '0') ruleFieldValue, IFNULL(o.rule_field_code, #{fieldKey}) ruleFieldCode, o.create_date createDate, o.modify_date modifyDate, o.data_status dataStatus FROM dj_config_rule_rank r LEFT JOIN dj_config_rule_item_one o ON r.id = o.rank_id AND o.module_id=#{moduleId} and o.rule_field_code = #{fieldKey}
AND o.type_id=#{typeId}
AND o.batch_code=#{batchCode}
ORDER BY o.create_date DESC LIMIT #{limit}

SELECT o.id, IFNULL(o.module_id, #{moduleId}) moduleId, IFNULL(o.batch_code, #{batchCodeTow}) batchCode, o.type_id, r.id rankId, r.name rankName, IFNULL(o.rule_field_name, #{fieldName}) ruleFieldName, IFNULL(o.rule_field_value, '0') ruleFieldValue, IFNULL(o.rule_field_code, #{fieldKey}) ruleFieldCode, o.create_date createDate, o.modify_date modifyDate, o.data_status dataStatus FROM dj_core_worker_type r LEFT JOIN dj_config_rule_item_one o ON r.id = o.rank_id AND o.module_id=#{moduleId} and o.rule_field_code = #{fieldKey}
AND o.batch_code=#{batchCode}
WHERE r.type != 7 and r.type != 5 AND r.type > 3 ORDER BY o.create_date DESC LIMIT #{limit}
