mapper.delete:
        delete from dj_core_house_flow_apply
        where
            house_id = #{houseId}
		and member_check = 0
		and worker_id = #{workerId}

mapper.select:
select
from dj_core_house_flow_apply where member_check = 0 and house_id = #{houseId}
and ( ((apply_type=1 or apply_type=2) and (supervisor_check = 1)) or apply_type=0 )

select
from dj_core_house_flow_apply where apply_type < 4 and house_flow_id = #{houseFlowId} and worker_id = #{workerId}
and ((supervisor_check=0 and apply_type!=0) or (supervisor_check=1 and member_check=0))

        select count(*)
        from dj_core_house_flow_apply
        where 1=1
		and house_id = #{houseId}
		and apply_type =0
		and worker_id = #{workerId}

select

from dj_core_house_flow_apply
where
to_days(create_date) = to_days(#{time})

    and house_flow_id = #{houseFlowId}


and apply_type =#{applyType}
and worker_id = #{workerId}
and supervisor_check !=2
order by create_date desc

select

from dj_core_house_flow_apply
where 1=1
and house_id = #{houseId}

    and worker_type_id=#{workerTypeId}

order by create_date DESC

SELECT f.id, f.house_flow_id AS houseFlowId, f.worker_id AS workerId, f.apply_type AS applyType, w.`name` AS workerTypeId, CONCAT( h.residential, '**栋**单元', IFNULL( h.number, '*' ), '号' ) AS houseId, f.suspend_day AS suspendDay, f.apply_dec AS applyDec, f.create_date AS createDate, f.modify_date AS modifyDate FROM dj_core_house_flow_apply f INNER JOIN dj_house h ON h.id = f.house_id INNER JOIN dj_core_worker_type w ON w.id = f.worker_type_id WHERE f.apply_type != 3 AND f.apply_type != 8 AND f.apply_type != 9
AND h.city_id = #{cityId}
AND DATE_SUB( CURDATE( ), INTERVAL 1 DAY ) <= date( f.create_date ) ORDER BY f.create_date DESC LIMIT 20
