mapper.delete:
        delete from dj_core_house_flow_apply
        where
            house_id = #{houseId}
		and member_check = 0
		and worker_id = #{workerId}

mapper.select:
        select count(*)
        from dj_core_house_flow_apply
        where 1=1
		and house_id = #{houseId}
		and apply_type =0
		and worker_id = #{workerId}

select 
from dj_core_house_flow_apply
where
    to_days(create_date) = to_days(#{time})
and house_flow_id = #{houseFlowId}
and apply_type =#{applyType}
and worker_id = #{workerId}
and supervisor_check !=2
order by create_date desc

select 
from dj_core_house_flow_apply
where 1=1
and  house_id = #{houseId}

    and worker_type_id=#{workerTypeId}

order by create_date DESC

SELECT FROM dj_core_house_flow_apply WHERE 1 = 1 AND house_id = #{houseId} AND (apply_type=0 OR supervisor_check=1)
and worker_type_id=#{workerTypeId}
ORDER BY create_date DESC
