mapper.select:
SELECT

from
dj_core_house_worker
where
work_type = #{workType}
and
worker_type_id = #{workerTypeId}
and
house_id = #{houseId}
GROUP BY is_select desc limit 1

select

from dj_core_house_worker
where house_id = #{houseId}
and worker_type = #{workerType}
and work_type=6
ORDER BY create_date DESC LIMIT 1

SELECT hf.id AS houseFlowId, hw.id AS houseWorkerId, h.id AS houseId, h.member_id AS memberId, hw.worker_type_id AS workerTypeId, CONCAT( h.residential, IFNULL( h.building, '*' ), '栋', IFNULL( CONCAT( h.unit, '单元' ), '' ), IFNULL( h.number, '*' ), '号' ) houseName, hf.release_time AS releaseTime, CONCAT( IFNULL( h.square, '0' ), 'm²' ) square, ( CASE WHEN hf.pause = 0 THEN '正常施工' ELSE '暂停施工' END ) AS isItNormal FROM dj_core_house_worker hw INNER JOIN dj_core_house_flow hf ON hw.house_id = hf.house_id AND hw.worker_type_id = hf.worker_type_id INNER JOIN dj_house h ON hw.house_id = h.id WHERE hw.worker_id = #{workerId}
AND hw.work_type !=2
AND hw.work_type IN ( 1, 6 ) AND hf.work_steta !=2 AND hf.work_steta !=6 AND h.visit_state != 3 AND h.visit_state != 4 ORDER BY hw.modify_date DESC

mapper.update:
UPDATE dj_core_house_worker
set data_status=1
WHERE house_id = #{houseId}
  AND worker_id = #{workerId}
