mapper.select:
SELECT c.id goodsId, c.name FROM dj_basics_goods c WHERE c.category_id=#{categoryId} and c.city_id=#{cityId}
and c.name like CONCAT('%',#{searchKey},'%')
AND EXISTS ( SELECT 1 FROM dj_basics_storefront_product t, dj_basics_product_template t1 WHERE t.prod_template_id = t1.id AND t.storefront_id = #{storefrontId} and t1.goods_id=c.id )
