mapper.select:
SELECT

from dj_deliver_cart
where
house_id = #{houseId}
and
worker_type_id = #{workerTypeId}
and
member_id = #{memberId}
GROUP BY product_id

mapper.update:
UPDATE dj_deliver_cart p1
SET p1.product_name =

    when id then #{item.name}

,p1.unit_name=

    when id then #{item.unitName}

,p1.price=

    when id then #{item.price}

,p1.category_id=

    when id then #{item.categoryId}

where product_id in

    #{item.id,jdbcType=BIGINT}
