mapper.select:
SELECT
FROM dj_deliver_order_item where house_id = #{houseId}
and order_id = #{orderId}
and product_name LIKE CONCAT('%',#{name},'%')
and category_id = #{categoryId}
ORDER BY create_date desc

mapper.update:
