mapper.select:
SELECT IFNULL(l.id,'-1') labelId, IFNULL(l.name,'其他') labelName, GROUP_CONCAT(c.id) categoryIds FROM dj_basics_goods_category c LEFT JOIN dj_basics_goods_category l ON l.id=c.parent_id WHERE c.id IN ( SELECT m.category_id FROM dj_deliver_order_item m WHERE m.order_id = #{orderId} and m.storefont_id = #{storefontId}
and m.product_type = #{productType}
GROUP BY m.category_id ) GROUP BY l.id ORDER BY l.id desc

SELECT m.id, p.is_agency_purchase steta, m.product_id productId, m.product_sn productSn, m.product_name productName, m.price, m.shop_count shopCount, m.unit_name unitName, (m.price * m.shop_count) totalPrice, m.create_date createDate, m.create_date createDate, m.product_type productType, m.data_status dataStatus, p.category_id categoryId, m.image, m.storefont_id storefontId, p.goods_id goodsId, p.value_name_arr attributeName FROM dj_deliver_order o INNER JOIN dj_deliver_order_item m ON o.id = m.order_id INNER JOIN dj_basics_storefront_product sp ON sp.id = m.product_id INNER JOIN dj_basics_product_template p ON sp.prod_template_id = p.id WHERE ( o.id = #{orderId} OR o.parent_order_id = #{orderId} )
and m.storefont_id = #{storefontId}
and m.product_type = #{productType}

SELECT
FROM dj_deliver_order_item where house_id = #{houseId}
and order_id = #{orderId}
and product_name LIKE CONCAT('%',#{name},'%')
and category_id = #{categoryId}
ORDER BY create_date desc

SELECT
	IFNULL(sum(t2.shop_count),0) shopCount,
	IFNULL(sum(t2.ask_count),0) askCount
FROM
	dj_deliver_order t1,
	dj_deliver_order_item t2
WHERE
	t1.id = t2.order_id
AND t1.storefont_id = #{storefrontId}
AND (
	t1.address_id = #{addressId}
	OR t1.house_id = #{houseId}
)
AND t2.product_id = #{productId}
