mapper.select:
SELECT

from dj_deliver_order
where
    storefont_id = #{storefontId}
and parent_order_id = #{parentOrderId}
and product_type = #{productType}

and
  parent_order_id!=id
LIMIT 1

SELECT

from dj_deliver_order
where
business_order_number = #{businessOrderNumber}

    and orderStatus = #{orderStatus}

ORDER BY create_date desc

SELECT count(0) FROM dj_deliver_order t1, dj_pay_business_order t2 WHERE t1.business_order_number = t2.number AND (t1.id = t1.parent_order_id or t1.parent_order_id is null) AND t2.state in(1,2) AND t1.data_status=0
AND t1.house_id=#{houseId}
AND EXISTS ( SELECT * FROM dj_member_address ma WHERE ma.member_id=#{memberId} AND ( ma.id = t1.address_id OR ma.house_id = t1.house_id ) )

select count(0) from dj_deliver_order_split dos LEFT JOIN dj_deliver_order_split_item dosi on dos.id = dosi.order_split_id LEFT JOIN dj_deliver_split_deliver ddsd on dosi.split_deliver_id = ddsd.id inner join dj_basics_storefront dbs on dbs.id=dosi.storefront_id where 1=1
AND dos.house_id = #{houseId}
AND EXISTS ( SELECT * FROM dj_member_address ma WHERE ma.member_id=#{memberId} AND ( ma.id = dos.address_id OR ma.house_id = dos.house_id ) )
and dos.data_status = 0 and dos.apply_status = 2
and dos.city_id = #{cityId}
and ( (dosi.split_deliver_id is null or dosi.split_deliver_id = '') or (dosi.split_deliver_id is not null and ddsd.shipping_state = 0) )

select count(0) from dj_deliver_split_deliver ddsd inner join dj_basics_storefront dbs on dbs.id=ddsd.storefront_id where 1=1
AND ddsd.house_id = #{houseId}
AND EXISTS ( SELECT * FROM dj_member_address ma WHERE ma.member_id=#{memberId} AND ( ma.id = ddsd.address_id OR ma.house_id = ddsd.house_id ) )
and ddsd.data_status = 0
and ddsd.city_id = #{cityId}
and (ddsd.shipping_state = 1 or ddsd.shipping_state = 7)
