mapper.update:
update dj_deliver_order_split_item
set split_deliver_id = #{splitDeliverId}
where id = #{id}

update dj_deliver_order_split_item
set receive = num
where split_deliver_id = #{splitDeliverId}

mapper.select:
SELECT sd.supplier_id supplierId, sd.supplier_name supplierName, sd.supplier_telephone supplierTelephone, si.house_id houseId, sum(si.sup_cost * si.num) totalPrice, sum( (si.price * si.num) - (si.sup_cost * si.num) ) totalProfit FROM `dj_deliver_order_split_item` si INNER JOIN dj_deliver_split_deliver sd ON si.split_deliver_id = sd.id AND sd.supplier_id IS NOT NULL
and sd.storefront_id =#{storefrontId}
WHERE si.house_id= #{houseId} GROUP BY sd.supplier_id, si.house_id

SELECT t.supplier_id supplierId, t.order_split_id orderSplitId, t1.is_non_platform_supplier isNonPlatformSupplier, t1.`name` supName, t1.telephone, t1.transportation_cost transportationCost, sum(t.sup_cost*t.num) totalPrice, sum(t.sup_stevedorage_cost) supStevedorageCost, max(IFNULL(t.is_delivery_install,'0')) isDeliveryInstall FROM dj_deliver_order_split_item t, dj_supplier t1 WHERE t.order_split_id = #{orderSplitId} AND t.supplier_id = t1.id
and t.split_deliver_id=#{splitDeliverId}
GROUP BY t.supplier_id

mapper.update:
update dj_deliver_order_split_item set split_deliver_id=#{splitDeliverId}
where order_split_id=#{orderSplitId} and supplier_id=#{supplierId}

    and t.split_deliver_id=#{oldSplitDeliverId}
