mapper.update:
update dj_deliver_order_split
set apply_status = 3
where id = #{orderSplitId}

mapper.select:
SELECT s.product_id productId FROM ( SELECT o.product_id FROM `dj_house_warehouse` o WHERE o.house_id = #{houseId} AND o.product_type =#{productType}
UNION ALL SELECT product_id FROM dj_deliver_cart WHERE house_id = #{houseId} AND worker_type_id = #{workerTypeId} AND product_type =#{productType} AND member_id = #{memberId}
) s GROUP BY s.product_id

SELECT s.category_id categoryId FROM ( SELECT o.category_id FROM `dj_house_warehouse` o WHERE o.house_id = #{houseId} AND o.product_type =#{productType}
UNION ALL SELECT category_id FROM dj_deliver_cart WHERE house_id = #{houseId} AND worker_type_id = #{workerTypeId} AND product_type =#{productType} AND member_id = #{memberId}
) s GROUP BY s.category_id
