mapper.update:
update dj_deliver_order_split
set apply_status = 3
where id = #{orderSplitId}

mapper.select:
SELECT s.product_id productId FROM ( SELECT oi.product_id FROM `dj_deliver_order` o INNER JOIN dj_deliver_order_item oi ON o.id = oi.order_id WHERE o.house_id = #{houseId}
AND o.worker_type_id =#{workerTypeId}
AND oi.product_type =#{productType} AND o.payment > 0 AND o.type = 2
UNION ALL SELECT product_id FROM dj_deliver_cart WHERE house_id = #{houseId} AND worker_type_id = #{workerTypeId} AND product_type =#{productType} AND member_id = #{memberId}
UNION ALL select DISTINCT dest_product_id as product_id from dj_deliver_product_change pc where pc.house_id = #{houseId} and pc.product_type = #{productType} ) s GROUP BY s.product_id

SELECT s.category_id categoryId FROM ( SELECT oi.category_id FROM `dj_deliver_order` o INNER JOIN dj_deliver_order_item oi ON o.id = oi.order_id WHERE o.house_id = #{houseId} AND oi.product_type =#{productType} AND o.worker_type_id =#{workerTypeId} AND o.payment > 0 AND o.type = 2
UNION ALL SELECT category_id FROM dj_deliver_cart WHERE house_id = #{houseId} AND worker_type_id = #{workerTypeId} AND product_type =#{productType} AND member_id = #{memberId}
UNION ALL select DISTINCT dest_product_id as product_id from dj_deliver_product_change pc where pc.house_id = #{houseId} and pc.product_type = #{productType} ) s GROUP BY s.category_id
