mapper.update:
update dj_deliver_order_split
set apply_status = 3
where id = #{orderSplitId}

mapper.select:
SELECT s.product_id productId FROM ( SELECT oi.product_id FROM `dj_deliver_order` o INNER JOIN dj_deliver_order_item oi ON o.id = oi.order_id WHERE o.house_id = #{houseId} AND o.worker_type_id =#{workerTypeId} AND oi.product_type =#{product_type} AND o.payment > 0 AND o.type = 2
UNION ALL SELECT product_id FROM dj_deliver_cart WHERE house_id = #{houseId} AND worker_type_id = #{workerTypeId} AND product_type =#{product_type} AND member_id = #{memberId}
) s GROUP BY s.product_id

SELECT s.category_id categoryId FROM ( SELECT oi.category_id FROM `dj_deliver_order` o INNER JOIN dj_deliver_order_item oi ON o.id = oi.order_id WHERE o.house_id = #{houseId} AND oi.product_type =#{product_type} AND o.worker_type_id =#{workerTypeId} AND o.payment > 0 AND o.type = 2
UNION ALL SELECT category_id FROM dj_deliver_cart WHERE house_id = #{houseId} AND worker_type_id = #{workerTypeId} AND product_type =#{product_type} AND member_id = #{memberId}
) s GROUP BY s.category_id
