mapper.select:
SELECT

FROM dj_deliver_product_change
where 1=1

	and house_id=#{houseId}


	and type=#{type}

ORDER BY create_date desc

SELECT COUNT(id) FROM dj_deliver_product_change
where 1=1

    and house_id=#{houseId}


    and src_product_id=#{srcProductId}


    and type=#{type}

LIMIT 1

mapper.update:
UPDATE dj_deliver_product_change p1
SET p1.src_product_name =

	when #{item.id} then #{item.name}

where src_product_id in

	#{item.id,jdbcType=BIGINT}

UPDATE dj_deliver_product_change p1
SET p1.dest_product_name =

	when #{item.id} then #{item.name}

where dest_product_id in

	#{item.id,jdbcType=BIGINT}
