mapper.select:
SELECT

FROM dj_deliver_product_change_order
where 1=1

	and house_id=#{houseId}


	and type=#{type}

ORDER BY create_date desc

SELECT prco.id, prco.number, type, prco.data_status as dataStatus, prco.create_date as createDate, prco.modify_date as modifyDate, house.id as houseId FROM dj_deliver_product_change_order as prco left JOIN dj_house as house on (prco.house_id = house.id ) where 1=1 and prco.type > 0
and prco.house_id = #{houseId}
and prco.create_date between #{beginDate} and #{endDate}
AND CONCAT( house.residential, IFNULL(house.building, '*'), '栋', IFNULL(CONCAT(house.unit, '单元'),'' ), IFNULL(house.number, '*'), '号' ) LIKE CONCAT('%',#{likeAddress},'%')
ORDER BY prco.create_date desc
