mapper.update:
update dj_deliver_split_deliver
set supState = 1
where id = #{splitDeliverId}

mapper.select:
SELECT b.id AS splitDeliverId, a.id AS orderSplitId, a.number, b.apply_state AS applyState, a.create_date AS createDate, b.supplier_id AS supplierId, a.house_id AS houseId, b.shipping_state AS shipState, b.ship_address AS shipAddress FROM dangjia_master.dj_deliver_order_split a INNER JOIN dangjia_master.dj_deliver_split_deliver b ON a.id=b.order_split_id WHERE b.data_status = 0 AND b.apply_state IS NOT NULL AND b.apply_state != 3 AND supplier_id=#{supplierId}
AND ( b.ship_address like CONCAT('%',#{searchKey},'%') OR a.number like CONCAT('%',#{searchKey},'%'))
and a.create_date between #{beginDate} and #{endDate}
ORDER BY a.create_date DESC

SELECT id, number AS number, create_date AS createDate, total_amount AS totalAmount, apply_money AS applyMoney, apply_state AS applyState, ship_address AS shipAddress, supplier_id AS supplierId FROM dj_deliver_split_deliver WHERE data_status = 0 and apply_state IS not NULL AND supplier_id=#{supplierId} AND apply_state = #{applyState}
and modify_date between #{beginDate} and #{endDate}
and ship_address like CONCAT('%',#{shipAddress},'%')
ORDER BY create_date DESC

SELECT id, number AS number, create_date AS createDate, total_amount AS totalAmount, apply_money AS applyMoney, apply_state AS applyState, ship_address AS shipAddress, supplier_id AS supplierId FROM dj_deliver_split_deliver WHERE id=#{id}
and modify_date between #{beginDate} and #{endDate}
and ship_address like CONCAT('%',#{shipAddress},'%')
