mapper.update:
update dj_deliver_split_deliver
set supState = 1
where id = #{splitDeliverId}

mapper.select:
SELECT id AS splitDeliverId, number AS number, total_amount AS totalAmount, apply_money AS applyMoney, apply_state AS applyState, ship_address AS shipAddress, supplier_id AS supplierId, supplier_telephone AS supMobile, supplier_name AS supName, modify_date AS modifyDate, create_date AS createDate FROM dj_deliver_split_deliver WHERE data_status = 0 and apply_state IS not NULL and apply_state != 3
and apply_state = #{applyState}
and apply_state != 0
and modify_date between #{beginDate} and #{endDate}
and ( ship_address like CONCAT('%',#{searchKey},'%') or ship_mobile like CONCAT('%',#{searchKey},'%') or ship_name like CONCAT('%',#{searchKey},'%') or supplier_name like CONCAT('%',#{searchKey},'%'))
ORDER BY create_date DESC
