mapper.select:
SELECT mr.id, mr.house_id houseId, mr.owner_name ownerName, mr.owner_mobile ownerMobile, mr.state, mr.create_date createDate, mr.steward_state stewardState, mr.owner_image ownerImage, mr.owner_remark ownerRemark, ( SELECT
IF ( m. NAME IS NULL OR m. NAME = '', m.nick_name, m. NAME ) FROM dj_member m WHERE m.id = mr.supervisor_id ) supervisorName, mr.service_remark serviceRemark, mr.steward_id stewardId, ( SELECT
IF ( m. NAME IS NULL OR m. NAME = '', m.nick_name, m. NAME ) FROM dj_member m WHERE m.id = mr.steward_id ) stewardName, ( SELECT m.mobile FROM dj_member m WHERE m.id = mr.steward_id ) stewardMobile, mr.since_purchase_amount sincePurchaseAmount, mr.owner_state ownerState, mr.steward_image stewardImage, mr.steward_remark stewardRemark, mr.remark, mr.steward_order_time stewardOrderTime FROM dj_maintenance_record mr WHERE mr.id=#{id}
