mapper.select:
SELECT bgc1.id, bgc1.name FROM dj_maintenance_record_product mrp INNER JOIN dj_basics_storefront_product bsp ON mrp.product_id = bsp.id INNER JOIN dj_basics_product_template bpt ON bsp.prod_template_id = bpt.id INNER JOIN dj_basics_goods_category bgc ON bgc.id = bpt.category_id INNER JOIN dj_basics_goods_category bgc1 ON bgc1.id = bgc.parent_top WHERE 1=1
mrp.maintenance_member_id =#{maintenanceMemberId}
AND mrp.maintenance_member_type =#{maintenanceMemberType}
AND mrp.maintenance_record_id =#{maintenanceRecordId}
AND mrp.house_id =#{houseId}
AND mrp.data_status = 0 GROUP BY bgc1.name,bgc1.id

SELECT

FROM
    dj_maintenance_record_product mrp
WHERE
    mrp.maintenance_member_id = #{memberId}
AND mrp.house_id = #{houseId}
and mrp.maintenance_member_type = #{maintenanceRecordType}
and mrp.worker_type_id = #{workerTypeId}
AND (
    mrp.maintenance_record_id IS NULL
    OR maintenance_record_id = ''
)
